// anise bread with sweet pear sauce
Recipe anise_bread_with_sweet_pear_sauce = create_recipe("anise bread with sweet pear sauce", dessert, "2 mini loaves", 20150514, 40);
set_description(&anise_bread_with_sweet_pear_sauce, "It's mini loaf craze! Love baking with the mini silicone pans I bought, I've been making mini everything. This time, I made some anise bread topped with a sweet sauce, and loaded with caramelized pear chunks.<br /><br />My bag of anise seeds has been sitting in my pantry, for way too long, begging to be given a purpose. The taste of anise pairs very well with pears.<br /><br />");
RecipePart anise_bread_with_sweet_pear_sauce_bread = create_part("bread");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Preheat oven to <u>325F</u>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Put <i>15 g (1 tbsp)</i> of <a href='ground_flax_seeds.html'>ground flax seeds</a> with <i>45 ml (3 tbsp)</i> of <a href='water.html'>water</a>, let thicken for <u>5 minutes</u> (this is your flax 'egg'). Mix in <i>50 g (1/4 cup)</i> of <a href='granulated_sugar.html'>sugar</a> and stir until dissolved. Add <i>5 g (1 tsp)</i> of <a href='ground_anise_seeds.html'>ground anise seeds</a>, and mix once more.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Add <i>90 g (3/4 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a> to a bowl with <i>7 ml (1 1/2 tsp)</i> of <a href='baking_powder.html'>baking powder</a>. Mix.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Pour the wet ingredients into the dry ones, while gradually adding a bit over <i>150 ml</i> of <a href='soy_milk.html'>soy milk</a>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Transfer evenly, to 2 mini loaf pans.");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &flax_seeds, "15 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &water, "45 ml");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &granulated_sugar, "50 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &anise_seeds, "5 g, ground");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &all_purpose_flour, "90 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &baking_powder, "7 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &soy_milk, "150 ml");
add_part(&anise_bread_with_sweet_pear_sauce, &anise_bread_with_sweet_pear_sauce_bread);
RecipePart anise_bread_with_sweet_pear_sauce_sauce = create_part("sauce");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Cut your <a href='bosc_pear.html'>bosc pear</a> into cubes.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Heat a pan at medium to high heat, add <i>60 ml (1/4 cup)</i> of <a href='canola_oil.html'>canola oil</a>. Once melted, add <i>15 g (1 tbsp)</i> of <a href='granulated_sugar.html'>sugar</a> and <i>15 ml (1 tbsp)</i> of <a href='maple_syrup.html'>maple syrup</a>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Once it starts to boil, add the cubed <a href='bosc_pear.html'>bosc pear</a> and lower the heat. Let it bathe in the sweet mixture for <u>3-5 minutes</u>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Pour the sauce over the 2 mini loaves, bake for <u>30 minutes</u>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Let cool and serve!");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &bosc_pear, "1");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &canola_oil, "15 ml");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &granulated_sugar, "15 g");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &maple_syrup, "15 ml");
add_part(&anise_bread_with_sweet_pear_sauce, &anise_bread_with_sweet_pear_sauce_sauce);

// arame soba
Recipe arame_soba = create_recipe("arame soba", pasta, "2 portions", 20151212, 40);
set_description(&arame_soba, "<b>Recipe location:</b> Montreal, QC. Canada<br /><br />After Japan opened itself to the world, Japanese cooks began to adapt western dishes in their own style. For instance in the west, people use ketchup as a condiment, but the Japanese use it as a base for tomato sauces. Spaghetti naporitan was created just after World War II, after Shigetada (the head chef at the Hotel New Grand in Yokohama) saw occupying soldiers eating it. Nowadays, you can find spaghetti seasoned with soy sauce, and served with seaweed.<br /><br /><b>About arame</b>: <a href='Arame.html'>Arame</a> is a species of kelp of a dark brown color, it has a mild, semi-sweet flavor and a firm texture. It can be reconstituted in about 5 minutes, and can be added to many kinds of dishes. It is high in calcium, iodine, iron, magnesium and vitamin A.<br /><br />");
RecipePart arame_soba_main = create_part("main");
add_instruction(&arame_soba_main, "Put <i>handful</i> of dried <a href='arame.html'>arame</a> in a bowl and cover with a cup of <a href='water.html'>water</a>. Let re-hydrate for at least <u>5 minutes</u>, drain.");
add_instruction(&arame_soba_main, "Bring a pot of <a href='water.html'>water</a> to a boil, add <i>2 portions</i> of soba (<a href='buckwheat_noodles.html'>buckwheat noodles</a>) and give them a quick stir so they go underwater. Reduce heat to medium and cook for <u>5 minutes</u>. Drain, rinse and transfer to a pot of cold water. Wash the noodles using your hands to remove the excess starch, drain and set aside.");
add_instruction(&arame_soba_main, "Julienne <i>1</i> <a href='carrot.html'>carrot</a>, chop <i>2 cloves</i> of <a href='garlic.html'>garlic</a> and cut <i>60g</i> of <a href='tempeh.html'>tempeh</a> into small cubes. I used <b>noble bean</b> original tempeh.");
add_instruction(&arame_soba_main, "Heat a pan with a drizzle of <a href='oil.html'>oil</a> at medium heat and sautee the <a href='garlic.html'>garlic</a> for a minute until fragrant. Add the <a href='tempeh.html'>tempeh</a> as well as the julienned <a href='carrot.html'>carrot</a> and cook for <u>2-3 minutes</u>.");
add_instruction(&arame_soba_main, "Pour the sauce and cook for an additional <u>5 minutes</u> until the <a href='tempeh.html'>tempeh</a> and <a href='carrots.html'>carrots</a> are cooked.");
add_instruction(&arame_soba_main, "Remove from heat and add the noodles, as well as the <a href='arame.html'>arame</a>. Stir well and divide into 2 plates, season with some <a href='sichuan_pepper.html'>sichuan pepper</a>.");
add_serving(&arame_soba_main, &arame, "handful");
add_serving(&arame_soba_main, &buckwheat_noodles, "2 portions");
add_serving(&arame_soba_main, &carrots, "1");
add_serving(&arame_soba_main, &garlic, "2 cloves");
add_serving(&arame_soba_main, &tempeh, "60 g");
add_serving(&arame_soba_main, &sichuan_peppercorns, "To taste");
add_part(&arame_soba, &arame_soba_main);

// balsamic banana ice cream
Recipe balsamic_banana_ice_cream = create_recipe("balsamic banana ice cream", dessert, "2 servings", 20150805, 120);
set_description(&balsamic_banana_ice_cream, "Banana ice cream is a simple alternative to dairy or coconut based desserts. It's a no-fuss recipe, that requires little preparation and waiting time.<br /><br />Making it is easy, and only requires putting bananas in the freezer. While waiting for them to harden up, prepare your balsamic coulis. If you've ever boiled balsamic vinegar, you know that it can sting your eyes. Making a balsamic vinegar reduction makes it thick and syrupy, and works well as a topping. I like the contrast of the bananas and vinegar.<br /><br />");
RecipePart balsamic_banana_ice_cream_balsamic_reduction = create_part("balsamic reduction");
add_instruction(&balsamic_banana_ice_cream_balsamic_reduction, "Put <i>240 ml (1 cup)</i> of <a href='balsamic_vinegar.html'>balsamic vinegar</a> in a non-stick pan.");
add_instruction(&balsamic_banana_ice_cream_balsamic_reduction, "Bring up to medium high heat, once it starts to boil bring down to medium low and let simmer until the vinegar has been reduced by a little more than half. Stir it on occasion. Make sure you have an open window or the overhead fan running because boiling vinegar has a really strong smell!");
add_instruction(&balsamic_banana_ice_cream_balsamic_reduction, "Transfer to a bowl and let cool completely, then store in the refrigerator. It will thicken when it gets cold. This recipe makes a lot of balsamic reduction, it's hard to make very little. It keeps in the refrigerator for a while so you can easily use it in other recipes. It's great when served over fruit.");
add_serving(&balsamic_banana_ice_cream_balsamic_reduction, &balsamic_vinegar, "250 ml");
add_part(&balsamic_banana_ice_cream, &balsamic_banana_ice_cream_balsamic_reduction);
RecipePart balsamic_banana_ice_cream_banana_ice_cream = create_part("banana ice cream");
add_instruction(&balsamic_banana_ice_cream_banana_ice_cream, "Peel and cut <i>2 whole</i> <a href='bananas.html'>bananas</a> into slices, lay slices flat onto a plate and put in the freezer for at least <u>2 hours</u>.");
add_instruction(&balsamic_banana_ice_cream_banana_ice_cream, "When properly frozen, put the banana slices in a blender — or food processor — with <i>60 ml (1/4 cup)</i> of <a href='soy_milk.html'>soy milk</a> and <i>1/4 tsp</i> of <a href='vanilla_extract.html'>vanilla extract</a>. Process until smooth.");
add_instruction(&balsamic_banana_ice_cream_banana_ice_cream, "Serve in individual bowls, with <i>5 ml (1 tsp)</i> or more of balsamic reduction.");
add_serving(&balsamic_banana_ice_cream_banana_ice_cream, &banana, "2");
add_serving(&balsamic_banana_ice_cream_banana_ice_cream, &soy_milk, "60 ml");
add_serving(&balsamic_banana_ice_cream_banana_ice_cream, &vanilla_extract, "1.25 ml");
add_part(&balsamic_banana_ice_cream, &balsamic_banana_ice_cream_banana_ice_cream);

// black sesame brittle
Recipe black_sesame_brittle = create_recipe("black sesame brittle", dessert, "10 pieces", 20141106, 20);
set_description(&black_sesame_brittle, "Sesame brittle looks impressive when served over desserts, like pieces of black coral. If you like the nutty taste of sesame seeds with a bit of sweet, you will love this recipe.<br /><br />It's simple to make, but requires all of your attention. The sugar syrup becomes solid when cool, which means every step needs to be done quickly. These are no-bake, and are ready to eat almost instantly.<br /><br />These should be served over desserts that aren't too sweet, the sweet of the sesame brittle can be a bit much. I suggest serving it with fruit, or <a href='#kanten_powder.html'>kanten</a> (agar agar based desserts).<br /><br />");
RecipePart black_sesame_brittle_brittle = create_part("brittle");
add_instruction(&black_sesame_brittle_brittle, "Prepare baking sheet lined with a baking mat, keep a flat spatula close by.");
add_instruction(&black_sesame_brittle_brittle, "Put the <i>45g (1/4 cup)</i> of <a href='brown_sugar.html'>brown sugar</a> and <i>1.25 ml (1/4 tsp)</i> of <a href='vanilla_extract.html'>vanilla extract</a> in a pot at medium heat.");
add_instruction(&black_sesame_brittle_brittle, "Stir constantly, until the sugar is completely dissolved.");
add_instruction(&black_sesame_brittle_brittle, "Add <i>30 g (1/4 cup)</i> of <a href='black_sesame_seeds.html'>black sesame seeds</a>. Mix thoroughly.");
add_instruction(&black_sesame_brittle_brittle, "This part is tricky, the mixture solidifies very quickly. You need to do this fast! Take the pot, and pour its contents out onto the baking sheet. With the spatula spread and flatten it out as much as you can.");
add_instruction(&black_sesame_brittle_brittle, "Cut the brittle while it's still warm, serve with fresh fruit!");
add_serving(&black_sesame_brittle_brittle, &brown_sugar, "45 g");
add_serving(&black_sesame_brittle_brittle, &vanilla_extract, "1.25 ml");
add_serving(&black_sesame_brittle_brittle, &black_sesame_seeds, "35 g");
add_part(&black_sesame_brittle, &black_sesame_brittle_brittle);

// black sesame rice pancakes
Recipe black_sesame_rice_pancakes = create_recipe("black sesame rice pancakes", breakfast, "12 servings", 20150226, 20);
set_description(&black_sesame_rice_pancakes, "Black sesame rice pancakes, for those who share my love of sesame.<br /><br />Photo is with a generous dollop of <a href='mango.html'>mango</a>, because sometimes I need a break from maple syrup — okay. No I don't, but here's something different anyway. The real reason I added mango, was because I enjoyed the contrast of colors. Most times, I top my pancakes with <a href='maple_syrup.html'>maple syrup</a>.<br /><br /><b>Substitutions:</b> For matcha pancakes, omit the black sesame seeds and add 15 ml of matcha powder.<br /><br />");
RecipePart black_sesame_rice_pancakes_pancakes = create_part("pancakes");
add_instruction(&black_sesame_rice_pancakes_pancakes, "Measure <i>300 ml (1 1/4 cups)</i> of <a href='soy_milk.html'>soy milk</a>, add <i>30 ml (2 tbsp)</i> of <a href='apple_cider_vinegar.html'>apple cider vinegar</a>. Let sit for <u>5 minutes</u>.");
add_instruction(&black_sesame_rice_pancakes_pancakes, "Heat <i>35 g (1/4 cup)</i> of <a href='black_sesame_seeds.html'>black sesame seeds</a>, in a non-stick pan at medium heat. Remove once they start to pop. Let cool, then crush into a powder with a mortar and pestle or immersion blender.");
add_instruction(&black_sesame_rice_pancakes_pancakes, "Mix the soured soy milk with <i>5 ml (1 tsp)</i> of <a href='vanilla_extract.html'>vanilla extract</a>, <i>15 ml (1 tbsp)</i> of <a href='canola_oil.html'>canola oil</a> and the ground <a href='black_sesame_seeds.html'>black sesame seeds</a>. Blend until you get a smooth grey liquid.");
add_instruction(&black_sesame_rice_pancakes_pancakes, "In another bowl, mix <i>130 g (1 cup)</i> of <a href='rice_flour.html'>rice flour</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>, <i>2.5 g (1/2 tsp)</i> of <a href='baking_powder.html'>baking powder</a>, <i>1.25 g (1/4 tsp)</i> of <a href='baking_soda.html'>baking soda</a> and <i>15 ml (1 tbsp)</i> of <a href='whole_cane_sugar.html'>whole cane sugar</a> together.");
add_instruction(&black_sesame_rice_pancakes_pancakes, "Pour wet ingredients into the dry ones, and mix until smooth. Make it as lump-free as you can.");
add_instruction(&black_sesame_rice_pancakes_pancakes, "Heat up non-stick pan at medium heat, add <i>15 ml (1 tbsp)</i> of <a href='canola_oil.html'>canola oil</a>. Spoon about <i>60 ml (1/4 cup)</i> of batter into the pan. Flip once the bottoms have browned, and little bubbles appear at the top.");
add_instruction(&black_sesame_rice_pancakes_pancakes, "Repeat for the rest of the pancake mix.");
add_serving(&black_sesame_rice_pancakes_pancakes, &soy_milk, "300 ml");
add_serving(&black_sesame_rice_pancakes_pancakes, &apple_cider_vinegar, "30 ml");
add_serving(&black_sesame_rice_pancakes_pancakes, &vanilla_extract, "5 ml");
add_serving(&black_sesame_rice_pancakes_pancakes, &canola_oil, "15 ml");
add_serving(&black_sesame_rice_pancakes_pancakes, &rice_flour, "130 g");
add_serving(&black_sesame_rice_pancakes_pancakes, &baking_powder, "2.5 g");
add_serving(&black_sesame_rice_pancakes_pancakes, &baking_soda, "1.25 g");
add_serving(&black_sesame_rice_pancakes_pancakes, &black_sesame_seeds, "35 g");
add_serving(&black_sesame_rice_pancakes_pancakes, &whole_cane_sugar, "15 g");
add_serving(&black_sesame_rice_pancakes_pancakes, &salt, "1.25 g");
add_part(&black_sesame_rice_pancakes, &black_sesame_rice_pancakes_pancakes);

// black sesame syrup
Recipe black_sesame_syrup = create_recipe("black sesame syrup", sauce, "2 servings", 20150601, 120);
set_description(&black_sesame_syrup, "I like making syrups, or juice reductions a lot. It's simple, and you can use it in many other recipes afterwards! Reductions that aren't sweetened with sugar, you can use as add-on to sauces, or alone as a 'glaze'.<br /><br />I used a technique by the cook <a href='https://discoginferno.wordpress.com/tag/sesame-seed-syrup' target='_blank'>Mike Case</a>, he made a white sesame syrup to use in cocktails. I liked not requiring a blender to make it, blending sesame seeds into a smooth liquid is hard, my immersion blender can't grind seeds finely. Boiling the seeds, and then straining them out is simple. And since you can re-use the seeds afterwards, there's no waste! The fact the seeds are toasted beforehand helps to bring out the nutty flavour, so whatever you do, don't skip that step!<br /><br />This syrup pairs well with ice cream, especially those with subtle flavors like coconut and vanilla. It's also delicious on fruit ice cream. To make fruit ice cream, slice fruit of choice thinly. Lay a sheet of parchment paper over a plate, and lay your fruit overtop (this will keep the fruit from sticking). Let them freeze for a few hours, then run them through an immersion blender. You can use almost any fruit to make it, but it works especially well for <a href='mango.html'>mango</a> and <a href='bananas.html'>bananas</a>.<br /><br />You can use the left-over seeds to bake into desserts, or to add as an extra topping.<br /><br />");
RecipePart black_sesame_syrup_syrup = create_part("syrup");
add_instruction(&black_sesame_syrup_syrup, "Put <i>140 g (1 cup)</i> of <a href='black_sesame_seeds.html'>black sesame seeds</a> in a pan, bring to medium heat and toast them until they start to pop.");
add_instruction(&black_sesame_syrup_syrup, "In a pot, combine <i>240 ml (1 cup)</i> of <a href='water.html'>water</a> with the <a href='black_sesame_seeds.html'>black sesame seeds</a>. Bring to a boil and let simmer for <u>10-15 minutes</u>.");
add_instruction(&black_sesame_syrup_syrup, "Strain liquid from sesame seeds using a cheese cloth or with a mesh strainer, squeezing out as much liquid as you can. Reserve sesame seeds for later use.");
add_instruction(&black_sesame_syrup_syrup, "Return liquid to pot, add <i>100 g (1/2 cup)</i> of {whole cane sugar}} and bring to medium heat. Stir to dissolve the sugar. Remove from heat and let cool.");
add_instruction(&black_sesame_syrup_syrup, "Serve of fresh fruit, or fruit ice cream.");
add_serving(&black_sesame_syrup_syrup, &black_sesame_seeds, "140 g");
add_serving(&black_sesame_syrup_syrup, &water, "240 ml");
add_serving(&black_sesame_syrup_syrup, &whole_cane_sugar, "100 g");
add_part(&black_sesame_syrup, &black_sesame_syrup_syrup);

// breaded veggie fingers
Recipe breaded_veggie_fingers = create_recipe("breaded veggie fingers", sidedish, "24 servings", 20150330, 90);
set_description(&breaded_veggie_fingers, "Was in the mood for some baked veggie fingers, didn't have tofu, but what I did have... was a jar full of chickpea flour. I prepared a batch of spicy, green scallion 'chickpea tofu' and coated it with breadcrumbs. With this, I prepared some baked chickpea fingers!<br /><br /><img src='../media/recipes/breaded_veggie_fingers_1.jpg'/><br /><br />The lemon juice adds flavour to the crumbs, no need for eggs or flour.<br /><br />I had these with sambal oelek, it's spicy but works well with the dish. I've been pairing this condiment with a lot of my foods lately, I'm sure these would also be good with a sweet mustard dip!<br /><br />Left-over breading will keep for weeks if stored in an airtight container.<br /><br />");
RecipePart breaded_veggie_fingers_chickpea_tofu = create_part("chickpea tofu");
add_instruction(&breaded_veggie_fingers_chickpea_tofu, "In a pot, add <i>360 ml (1 1/2 cups)</i> of <a href='vegetable_bouillon.html'>vegetable bouillon</a> and bring to a rolling boil.");
add_instruction(&breaded_veggie_fingers_chickpea_tofu, "Mix <i>110 g (1 cup)</i> of <a href='chickpea_flour.html'>chickpea flour</a> with <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>, <i>2.5 g (1/2 tsp)</i> of <a href='ground_turmeric.html'>ground turmeric</a> and <i>15 g (1 tbsp)</i> of <a href='chili_pepper_flakes.html'>chili pepper flakes</a>. Add <i>360 ml (1 1/2 cups)</i> of <a href='water.html'>water</a> and stir until the mixture is lump-free.");
add_instruction(&breaded_veggie_fingers_chickpea_tofu, "Lower heat to medium, and pour in chickpea batter. Whisk continuously for <u>5-10 minutes</u> until the mixture has thickened.");
add_instruction(&breaded_veggie_fingers_chickpea_tofu, "Remove from heat, add <i>2 stalks</i> of chopped <a href='scallions.html'>scallions</a> and mix well. Pour into a 8x8 baking dish lined with a baking mat. Smooth out top with the back of a wooden spoon.");
add_instruction(&breaded_veggie_fingers_chickpea_tofu, "Let cool and set for <u>1 hour</u>, then refrigerate for a little while longer before cutting. <i>Cut into 24 strips</i>.");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &vegetable_bouillon, "360 ml");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &chickpea_flour, "110 g");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &salt, "1.25 g");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &ground_turmeric, "2.5 g");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &chili_pepper_flakes, "15 g");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &water, "360 ml");
add_serving(&breaded_veggie_fingers_chickpea_tofu, &scallions, "2 stalks");
add_part(&breaded_veggie_fingers, &breaded_veggie_fingers_chickpea_tofu);
RecipePart breaded_veggie_fingers_breading = create_part("breading");
add_instruction(&breaded_veggie_fingers_breading, "Take <i>5 slices</i> of bread, and tear them into small bits using your fingers. It's easier to do that with a food processor, but not necessary.");
add_instruction(&breaded_veggie_fingers_breading, "Preheat your oven at <u>150 °C (300 °F)</u>. Lay the crumbs down in a thin layer over a baking sheet, laying them out this way ensures even browning. Bake the bread bits for <u>5 minutes</u>. After that time, shake them around the tray, and bake them for an additional <u>5 minutes</u>. Keep an eye on me them to make sure they don't burn! Let cool.");
add_serving(&breaded_veggie_fingers_breading, &fresh_bread, "5 slices");
add_part(&breaded_veggie_fingers, &breaded_veggie_fingers_breading);
RecipePart breaded_veggie_fingers_breading_mix = create_part("breading mix");
add_instruction(&breaded_veggie_fingers_breading_mix, "Preheat oven to <u>220 °C (425 °F)</u>.");
add_instruction(&breaded_veggie_fingers_breading_mix, "Mix <i>150 g (1 cup)</i> of dried breadcrumbs with <i>10g (1/4 cup)</i> of <a href='nutritional_yeast.html'>nutritional yeast</a>. Spread out thinly onto a plate.");
add_instruction(&breaded_veggie_fingers_breading_mix, "Pour <i>45 ml (3 tbsp)</i> of <a href='lemon_juice.html'>lemon juice</a> in a separate plate.");
add_instruction(&breaded_veggie_fingers_breading_mix, "Take a slice of the <a href='chickpea_tofu.html'>chickpea tofu</a> and dip into the <a href='lemon_juice.html'>lemon juice</a>, making sure all sides are covered, then dip into the breadming mix. Make sure all sides are well coated.");
add_instruction(&breaded_veggie_fingers_breading_mix, "Place on baking sheet lined a baking mat, season with <a href='black_pepper.html'>black pepper</a> and <a href='salt.html'>salt</a>. Spray lightly with <a href='olive_oil.html'>olive oil</a>.");
add_instruction(&breaded_veggie_fingers_breading_mix, "Bake for <u>15 minutes</u>, flip, bake for another <u>15 minutes</u>. Serve with dipping sauce of choice!");
add_serving(&breaded_veggie_fingers_breading_mix, &nutritional_yeast, "10 g");
add_serving(&breaded_veggie_fingers_breading_mix, &lemon_juice, "50 ml");
add_part(&breaded_veggie_fingers, &breaded_veggie_fingers_breading_mix);

// brownies
Recipe brownies = create_recipe("brownies", basic, "24 servings", 20150324, 60);
set_description(&brownies, "A classic brownie recipe.<br /><br /><b>Substitutions</b><br /><br /><b>Fat :</b> Use 113 g (~1/2 cup) vegan butter instead of oil. It's possible to use less fat, but you can only substitute half the amount of fat before it affects the texture. For example, you can use 45 ml (1/8th cup) of vegetable oil with 60 g (45 ml) of pumpkin puree.<br /><br /><b>Chocolate : </b>Use <a href='unsweetened_cocoa_powder.html'>unsweetened cocoa powder</a> instead of bar chocolate, for every 30 g (1 oz) of chocolate add 45 g (3 tbsp) of cocoa powder plus 15 g (1 tbsp) of vegan butter.<br /><br />Final tip: To prevent burning the bottom of your brownies, place the pan on a preheated cookie sheet. Brownie tips courtesy of <a href='http://www.kitchenproject.com/history/Brownies/BrownieTips.htm#Mixing'>the kitchen project</a>.");
RecipePart brownies_brownies = create_part("brownies");
add_instruction(&brownies_brownies, "Preheat oven to <u>180 °C (350 °F)</u>.");
add_instruction(&brownies_brownies, "Melt <i>90 ml</i> of <a href='canola_oil.html'>canola oil</a> (or 113 g | 1/2 cup vegan butter) and <i>250 g</i> of coarsely chopped unsweetened <a href='dark_chocolate.html'>chocolate</a> together. The best way to do this is in a double boiler or any small pan placed over a pot of gently simmering water. If you're an experienced baker, you can place the oil/butter and chocolate directly in a saucepan over a low flame. Be sure to stir the mixture constantly. Remove from heat, transfer to a bowl and let cool.");
add_instruction(&brownies_brownies, "While the chocolate is cooling down, in a small bowl add <i>45 g (3 tbsp)</i> of <a href='ground_flax_seeds.html'>ground flax seeds</a> with <i>135 ml (9 tbsp)</i> of <a href='water.html'>water</a>. Let thicken for <u>5 minutes</u>.");
add_instruction(&brownies_brownies, "Pour the flax 'eggs' into the melted chocolate/oil, then add <i>75 g (5 tbsp)</i> of <a href='brown_sugar.html'>brown sugar</a> and 100 g (1/2 cup) of <a href='granulated_sugar'>granulated sugar</a>. Stir well, without overmixing.");
add_instruction(&brownies_brownies, "Add <i>1.25 g of salt</i> and <i>140 g (1 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a> and mix just long enough to blend them.");
add_instruction(&brownies_brownies, "Pour into a greased 8X8 baking dish. To improve the flavor of the brownies, place the unbaked batter in refrigerator <u>overnight (or a few hours)</u>. If you don't want to wait, place baking dish in oven and bake for <u>~20 minutes</u>. Add <u>2 minutes</u> if using vegan butter. To check if it's ready, insert a toothpick into the center, if it comes out with a few moist crumbs attached to it, it's ready. Remove from oven and let cool. <i>Cut in 24 small squares</i>.");
add_serving(&brownies_brownies, &canola_oil, "90 ml");
add_serving(&brownies_brownies, &dark_chocolate, "250 g");
add_serving(&brownies_brownies, &flax_seeds, "45 g");
add_serving(&brownies_brownies, &water, "135 ml");
add_serving(&brownies_brownies, &brown_sugar, "75 g");
add_serving(&brownies_brownies, &granulated_sugar, "100 g");
add_serving(&brownies_brownies, &salt, "1.25 g");
add_serving(&brownies_brownies, &all_purpose_flour, "140 g");
add_part(&brownies, &brownies_brownies);

// carrot kinpira onigirazu
Recipe carrot_kinpira_onigirazu = create_recipe("carrot kinpira onigirazu", dinner, "4 servings", 20150731, 20);
set_description(&carrot_kinpira_onigirazu, "An onigirazu is a Japanese rice ball sandwich, or a 'lazy onigiri'.<br /><br />The word onigiri (or nigiru) means to press into shape using your hands, while \"razu\" means the opposite. Free form onigiri! This is perfect for people who have a hard time making rice balls, as is the case for me. Onigirazu has the same great taste, without the fear of imperfection.<br /><br />This recipe is perfect when you don't have the right type of rice available for onigiri. You can use just about any type, I tested it out a few different kinds. The seaweed wrapping will keep it together, thus eliminating the need for sticky rice.<br /><br />The rice was seasoned with miso for added flavour, and was filled with carrot 'kinpira' — 'Kinpira' means \"sauteed\" (usually with a mixture of mirin soy sauce and chili peppers.) It's a sweet and spicy dish that is often served in bentos.<br /><br /><img src='../media/recipes/carrot_kinpira_onigirazu_2.jpg'/><br /><br />You should try and make your own version of onigirazu at home! As I said, it's no-fail.<br /><br />");
RecipePart carrot_kinpira_onigirazu_kinpira_carrot = create_part("kinpira carrot");
add_instruction(&carrot_kinpira_onigirazu_kinpira_carrot, "Peel and cut <i>2 large</i> <a href='carrot.html'>carrot</a> into thin strips using a julienne peeler (a knife will do, but it will take longer).");
add_instruction(&carrot_kinpira_onigirazu_kinpira_carrot, "Stir these ingredients together to make the sauce for the kinpira: <i>10 ml (2 tsp)</i> of <a href='maple_syrup.html'>maple syrup</a>, <i>10 ml (2 tsp)</i> of <a href='mirin.html'>mirin</a> and <i>30 ml (2 tsp)</i> of <a href='soy_sauce.html'>soy sauce</a>.");
add_instruction(&carrot_kinpira_onigirazu_kinpira_carrot, "Heat <u>15 ml (1 tbsp)</u> of <a href='sesame_oil.html'>sesame oil</a> in a pan at medium heat, add the carrot strips and cook for <u>2-3 minutes</u>. Stir in <i>5 g (1 tsp)</i>  of <a href='chili_pepper_flakes.html'>chili pepper flakes</a> as well as the sauce prepared in the previous step.");
add_instruction(&carrot_kinpira_onigirazu_kinpira_carrot, "Cook until no liquid remains. Let cool.");
add_serving(&carrot_kinpira_onigirazu_kinpira_carrot, &carrots, "2");
add_serving(&carrot_kinpira_onigirazu_kinpira_carrot, &maple_syrup, "10 ml");
add_serving(&carrot_kinpira_onigirazu_kinpira_carrot, &mirin, "10 ml");
add_serving(&carrot_kinpira_onigirazu_kinpira_carrot, &soy_sauce, "30 ml");
add_serving(&carrot_kinpira_onigirazu_kinpira_carrot, &sesame_oil, "15 ml");
add_serving(&carrot_kinpira_onigirazu_kinpira_carrot, &chili_pepper_flakes, "5 g");
add_part(&carrot_kinpira_onigirazu, &carrot_kinpira_onigirazu_kinpira_carrot);
RecipePart carrot_kinpira_onigirazu_rice = create_part("rice");
add_instruction(&carrot_kinpira_onigirazu_rice, "Wash <i>190g (~1 cup)</i> of <a href='short_grain_white_rice.html'>short grain white rice</a> to release excess starch, until water runs clear. Soak for <u>30 min</u> (in summer) and <u>2h</u> (in winter).");
add_instruction(&carrot_kinpira_onigirazu_rice, "Add <i>350 ml (1 1/2 cups)</i> of <a href='water.html'>water</a> into a pot, add the rice and <i>15 g (1 tbsp)</i> of <a href='white_miso.html'>white miso</a>. Stir to dissolve miso into liquid/rice. Bring pot contents to a boil, turn heat to low and cover pot with lid. Cook for <u>10 minutes</u>, turn heat off and let rest for <u>10 min</u> before uncovering.");
add_serving(&carrot_kinpira_onigirazu_rice, &short_grain_white_rice, "1 cup");
add_serving(&carrot_kinpira_onigirazu_rice, &white_miso, "15 g");
add_serving(&carrot_kinpira_onigirazu_rice, &water, "350 ml");
add_part(&carrot_kinpira_onigirazu, &carrot_kinpira_onigirazu_rice);
RecipePart carrot_kinpira_onigirazu_wrapper = create_part("wrapper");
add_instruction(&carrot_kinpira_onigirazu_wrapper, "Lay out <i>4 pieces</i> of <a href='nori_sheets.html'>nori sheets</a>, rough side up. Lay out some rice in the center of the 4 sheets, then divide the carrot kinpira into 4 portions and lay over the rice. Cover the carrots with the remaining rice.");
add_instruction(&carrot_kinpira_onigirazu_wrapper, "Now, take one corner of the nori sheet and fold over into the middle, do the same for the opposing corner. Then, repeat for the other 2 corners. Press down gently.");
add_instruction(&carrot_kinpira_onigirazu_wrapper, "Wait until the nori has softened and then cut into the middle! Serve as is.");
add_serving(&carrot_kinpira_onigirazu_wrapper, &nori_sheets, "4");
add_part(&carrot_kinpira_onigirazu, &carrot_kinpira_onigirazu_wrapper);

// cheese
Recipe cheese = create_recipe("cheese", basic, "2 servings", 20190609, 60);
set_description(&cheese, "I used to have a cheese recipe on this website, but it failed me a few times after making it, which made it a poor recipe indeed. This time, I've experiemented a lot more, removing and adding ingredients.<br /><br />This is a basic cheese recipe, that you can add onto if you want different flavors. It's simple to prepare, and ready under an hour. Another great thing about it is that it's completely nut-free!<br /><br />This cheese can be grated, it holds its shape well enough so it can be used as a topping on pizza — yay!<br /><br />In this recipe I use <a href='kanten.html'>kanten</a>, which is similar to <a href='agar_agar.html'>agar agar</a> except that it's made from a different type of red algae (tengusa). It can be swapped 1:1 in a recipe, although the resulting texture will not be the same. Adding agar agar will make the cheese softer, so I highly recommend using kanten if available.<br /><br />In this recipe, you can omit the sunflower seeds and 240 ml of water, and replace it with 240 ml of soy milk instead. This will result in a lighter cheese.");
RecipePart cheese_cheese = create_part("cheese");
add_instruction(&cheese_cheese, "Soak <i>140 g (1 cup)</i> of <a href='sunflower_seeds.html'>raw unsalted sunflower seeds</a> in water for <u>2 hours</u>, or overnight.");
add_instruction(&cheese_cheese, "Drain and rinse sunflower seeds, and blend with <i>240 ml (1 cup)</i> of <a href='water.html'>water</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>, <i>30 g (2 tbsp)</i> of <a href='nutritional_yeast.html'>nutritional yeast</a>, <i>2.5 g (1/2 tsp)</i> of <a href='onion_powder.html'>onion powder</a>, <i>5 ml (1 tsp)</i> of <a href='apple_cider_vinegar.html'>apple cider vinegar</a> and <i>5 g (1 tsp)</i> of <a href='white_miso.html'>white miso</a>.");
add_instruction(&cheese_cheese, "In a small bowl, dissolve <i>30 g (2 tbsp)</i> of <a href='arrowroot_starch.html'>arrowroot starch</a> (or 30 g of tapioca starch) with <i>30 ml (2 tbsp) of water</i>. Keep aside.");
add_instruction(&cheese_cheese, "Heat a saucepan at medium heat, pour <i>120 ml (1/2 cup)</i> of <a href='soy_milk.html'>soy milk</a> (or water) and sprinkle <i>6 g (~1 tbsp)</i> of <a href='kanten_powder.html'>kanten powder</a> (or equal amount of <a href='agar_agar_powder.html'>agar agar powder</a>)</a>. Stir well.");
add_instruction(&cheese_cheese, "When content starts to boil, lower to a simmer and pour the sunflower cheese mix into it. Stir, for <u>1-2 minutes</u>, then add the arrowroot starch mix. Continue whisking until mixture is thick and shiny. Pour contents of pan into container of choice (I like to use 2 small bowls).");
add_instruction(&cheese_cheese, "Let mixture rest for <u>2 hours</u>.");
add_instruction(&cheese_cheese, "Flip bowls upside down, scoop out cheese and enjoy! This cheese can be grated onto pizza too!");
add_serving(&cheese_cheese, &sunflower_seeds, "140 g");
add_serving(&cheese_cheese, &water, "240 ml");
add_serving(&cheese_cheese, &salt, "1.25 g");
add_serving(&cheese_cheese, &nutritional_yeast, "30 g");
add_serving(&cheese_cheese, &onion_powder, "2.5 g");
add_serving(&cheese_cheese, &apple_cider_vinegar, "5 ml");
add_serving(&cheese_cheese, &white_miso, "5 g");
add_serving(&cheese_cheese, &arrowroot_starch, "30 g");
add_serving(&cheese_cheese, &soy_milk, "120 ml");
add_serving(&cheese_cheese, &kanten_powder, "6 g");
add_part(&cheese, &cheese_cheese);

// cheese and spinach ravioli
Recipe cheese_and_spinach_ravioli = create_recipe("cheese and spinach ravioli", pasta, "2 people", 20171610, 40);
set_description(&cheese_and_spinach_ravioli, "<b>Recipe location:</b> Whangarei, New Zealand.<br /><br />We have arrived in New Zealand, the land of plenty. All of the foods that we like and miss are here. Foods like nutritional yeast, miso and soba (to name a few). With a fully re-stocked pantry, I started to make faux-cheese again, a recipe from <b>Vegan Richa</b> that is simple to make and that I love. The recipe is for a cheese that can be cut into wedges, or that can be grated over pizza. I had an idea to use this recipe to make filling for ravioli, the difference being that I won't add any agar agar (a seaweed based powder that makes liquids gellify). Making your own dough is simple, the whole process will take you less than 40 minutes.<br /><br /><img src='../media/recipes/cheese_and_spinach_ravioli_1.jpg'/><br /><br />I got the idea to make ravioli from an old 70's book about the cooking of Italy. This book is one of many that we found in a thrift shop here in Whangarei, we bought all the ones that we could find. Devine & I like picking through them, drawing inspiration from the images and ingredients.<br /><br /><img src='../media/recipes/cheese_and_spinach_ravioli_2.jpg'/><br /><br />A lot of the recipes in these books use meat and dairy, but it's easy to swap these ingredients out for something else. In New Zealand, the groceries are plentiful and finding everything we need is a breeze. We hope you enjoy this recipe, and that you try and make <a href='http://www.veganricha.com/2014/08/almond-milk-pepper-jack-cheese-vegan-glutenfree-recipe.html' target='_blank'>Richa's original pepper jack cheese recipe</a> too.<br /><br />");
RecipePart cheese_and_spinach_ravioli_filling = create_part("filling");
add_instruction(&cheese_and_spinach_ravioli_filling, "For the filling, mix all of the dry ingredients into a bowl, then add all the wet ones (including the chopped <a href='spinach.html'>spinach</a>) and stir well until evenly mixed.");
add_instruction(&cheese_and_spinach_ravioli_filling, "Heat a pan at medium heat, pour the filling into it. The mixture will thicken. When it does, transfer it back to a bowl and keep it aside for later.");
add_serving(&cheese_and_spinach_ravioli_filling, &chickpea_flour, "15 ml");
add_serving(&cheese_and_spinach_ravioli_filling, &nutritional_yeast, "10 g");
add_serving(&cheese_and_spinach_ravioli_filling, &arrowroot_starch, "22 g");
add_serving(&cheese_and_spinach_ravioli_filling, &garlic_powder, "1.25 g");
add_serving(&cheese_and_spinach_ravioli_filling, &onion_powder, "1.25 g");
add_serving(&cheese_and_spinach_ravioli_filling, &chili_pepper_flakes, "1.25 g");
add_serving(&cheese_and_spinach_ravioli_filling, &spinach, "20 g");
add_serving(&cheese_and_spinach_ravioli_filling, &lemon_juice, "5 ml");
add_serving(&cheese_and_spinach_ravioli_filling, &apple_cider_vinegar, "2.5 ml");
add_serving(&cheese_and_spinach_ravioli_filling, &soy_milk, "180 ml");
add_part(&cheese_and_spinach_ravioli, &cheese_and_spinach_ravioli_filling);
RecipePart cheese_and_spinach_ravioli_ravioli = create_part("ravioli");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "To make the dough, mix <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> with <i>120 g (1 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>. Add <i>160 ml</i> of water, knead the dough on a floured surface, working in extra flour if the dough is too sticky. Once you have a smooth ball of dough, wrap it up and let it rest for at least <u>10 minutes</u>.");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "Divide the dough into two pieces, and roll out the first half of the dough to make it as thin as possible — and as rectangular as possible. Roll out the second piece of dough into a similar size and thickess.");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "On the first sheet of pasta, place a mound of filling of the faux-cheese and spinach mixture every 5 cm across and down the pasta (spaced out to look like a checker board).");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "Dip your finger into a bowl of water, and make a line inbetween each row of filling (the water acts as a bond to hold the ravioli together).");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "Place the second sheet of rolled-out pasta on top of the first one, pressing down firmly around the filling and along the wetted lines.");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "With a knife (or ravioli cutter, if you feel fancy), cut the pasta into squares along the wetted lines. Keep separated pieces of ravioli aside.");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "Bring a pot of water to a boil, and add the ravioli. Stir them gently to keep them from sticking to another another or on the bottom of the pot. Cook for <u>7-8 minutes</u> or until fork tender.");
add_instruction(&cheese_and_spinach_ravioli_ravioli, "Serve with a light sauce, we made one with <a href='chilis_pepper_flakes.html'>chilis pepper flakes</a>, fresh <a href='basil.html'>basil</a> and <a href='olive_oil.html'>olive oil</a>. A light tomato sauce would also be delicious.");
add_serving(&cheese_and_spinach_ravioli_ravioli, &all_purpose_flour, "120 g");
add_serving(&cheese_and_spinach_ravioli_ravioli, &water, "160 ml");
add_serving(&cheese_and_spinach_ravioli_ravioli, &salt, "1.25 g");
add_part(&cheese_and_spinach_ravioli, &cheese_and_spinach_ravioli_ravioli);

// basic black bread
Recipe basic_black_bread = create_recipe("basic black bread", bread, "1 loaf", 20150111, 140);
set_description(&basic_black_bread, "Introducing, my basic black bread recipe. This bread is super light and fluffy, it's great great for morning toast or sandwiches.<br /><br />I've been reading up a lot about bread, there's so many kinds out there. I wanted to understand how the ingredients we add, can change the texture of the bread. Also read about the differences in temperature, to knead or not to knead etc.<br /><br />Truth is, it depends on the type of bread you want.<br /><br /><img src='../media/recipes/basic_black_bread_1.jpg'/><br /><br />I wanted to make a sandwich bread with a light crumb, the kind that bounces back when touched.<br /><br />The one I made this time has more fat, which in turn makes it softer and fluffier. The fat that you use will also change the texture/taste of the bread. A lot of people wont like the idea of adding 'fat' to a recipe, know that fat isn't synonymous with unhealthy. Too much of it can be bad, but in moderation there really isn't anything to worry about. It also depends on what fat you choose, there are good and bad kinds of fat.<br /><br />There are many things you can do to help soften your bread, like brushing the outside with a little oil or fat. Do this as soon as you take it out of the oven, it will make the outside less crunchy. You can also substitute nut milk for the water, if you want a richer taste. There are so many different things to think about when baking!<br /><br />I made this loaf for a brunch I had with friends, we wanted to have fondue with a set I got as a gift during the holidays. We  cut the loaf into cubes, and dunked them in! Soft bread is perfect for fondue!<br /><br /><img src='../media/recipes/basic_black_bread_2.jpg'/><br /><br />So there you have it! A basic black bread!<br /><br /><b>Burger buns:</b> Repeat steps 1 to 6 in the recipe below. Instead of forming into a 'cigar' shape at step 7,divide into 8 pieces and shape into a tight ball. Sprinkle baking sheet with some cornmeal and put buns on top. Cover and let rise for another 45 minutes in the warmed oven. Take buns out of oven, brush lightly with 15 ml (1 tbsp) of coconut oil (for browning) and put white sesame seeds on top. Bake uncovered for 20 minutes at 180 °C (350 °F). Let cool on a pile of towels or cooling rack.<br /><br /><b>Hot dog buns:</b> Repeat steps 1 to 6. Divide through into 15 pieces and shape into small cylinders (or general elongated hot dog shape). Sprinkle baking sheet with some cornmeal and put buns on top. Cover and let rise for another 45 minutes in the warmed oven. Take buns out of oven, brush lightly with coconut oil. Bake uncovered for 20 minutes< at 180 °C (350 °F). Let cool on a pile of towels or cooling rack.<br /><br />");
RecipePart basic_black_bread_bread = create_part("bread");
add_instruction(&basic_black_bread_bread, "In a large bowl, stir <i>15 ml (1 tbsp)</i> of <a href='maple_syrup.html'>maple syrup</a> in <i>240 ml (1 cup)</i> of <a href='warm_water.html'>warm water</a> until dissolved. Sprinkle <i>5 g (1 tsp)</i> of <a href='active_dry_yeast.html'>active dry yeast</a>, let sit for <u>10 minutes</u>.");
add_instruction(&basic_black_bread_bread, "Sift <i>180 g (1 1/2 cups)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>, <i>180 g (1 1/2 cups)</i> of <a href='whole_grain_flour.html'>whole grain flour</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> and <i>5 g (1 tsp)</i> of <a href='bamboo_charcoal_powder.html'>bamboo charcoal powder</a> together in a separate bowl. Once flours are mixed evenly enough.");
add_instruction(&basic_black_bread_bread, "Add <i>15 ml (1 tbsp)</i> of <a href='canola_oil.html'>canola oil</a> to the water mix, then stir in <i>60 g (1/2 cup)</i> of the flour mix with a wooden spoon. Keep adding flour a <u>60 g (1/2 cup)</u> at a time, until the dough stops sticking to the sides.");
add_instruction(&basic_black_bread_bread, "Put ball of dough on lightly floured counter-top and start kneading, add a bit of flour everytime it starts to stick to your hands. Knead for <u>15 minutes</u>. You may not use up all of your flour, if there's still a lot left and your dough is nice smooth don't add any more. Don't want to risk drying it out.");
add_instruction(&basic_black_bread_bread, "Heat up oven at the very lowest setting. This will be our warm place for bread rising.");
add_instruction(&basic_black_bread_bread, "Grease up a bowl and put the dough, rolling it once to cover all sides. Cover with a cloth and place in oven. <i>Let rise for 45 minutes</i>.");
add_instruction(&basic_black_bread_bread, "Your bread should have doubled in size. Take bread out of oven, knead a few times and shape into a 25-28 cm cigar-shape. Sprinkle some <a href='corn_semolina.html'>corn semolina</a> on a baking sheet and place bread on top - this will keep it from sticking. Slash the top of the bread in the middle with a knife, doing this will keep the ends of the bread from splitting. Mist top of bread lightly with <a href='water.html'>water</a>, then add <a href='white_sesame_seeds.html'>white sesame seeds</a>. Press lightly to make them stick. Cover loaf with a cloth, put back in oven to rise for an additional <u>45 minutes</u>. If you don't want a traditional bread shape, put bread in a greased loaf pan to rise.");
add_instruction(&basic_black_bread_bread, "Turn oven up to <u>180 °C (350 °F)</u> . Bake for <a href='30_minutes.html'>30 minutes</a>, or until it sounds hollow when tapped at the bottom.");
add_instruction(&basic_black_bread_bread, "Brush sides lightly with <a href='coconut_oil.html'>coconut oil</a>, then let cool on a pile of towels or a cooling rack.");
add_serving(&basic_black_bread_bread, &maple_syrup, "15 ml");
add_serving(&basic_black_bread_bread, &water, "240 ml, warm");
add_serving(&basic_black_bread_bread, &active_dry_yeast, "5 g");
add_serving(&basic_black_bread_bread, &canola_oil, "15 ml");
add_serving(&basic_black_bread_bread, &all_purpose_flour, "180 g");
add_serving(&basic_black_bread_bread, &whole_wheat_flour, "180 g");
add_serving(&basic_black_bread_bread, &bamboo_charcoal_powder, "5 g");
add_serving(&basic_black_bread_bread, &salt, "1.25 g");
add_serving(&basic_black_bread_bread, &white_sesame_seeds, "5 g");
add_serving(&basic_black_bread_bread, &corn_semolina, "15 g");
add_serving(&basic_black_bread_bread, &coconut_oil, "15 ml");
add_part(&basic_black_bread, &basic_black_bread_bread);

// basic toothpaste
Recipe basic_toothpaste = create_recipe("basic toothpaste", lifestyle, "1 jar", 20161105, 5);
set_description(&basic_toothpaste, "I've always liked making things from scratch, especially when cooking. I enjoy the process. Making it myself means that I control the quality and quantity of each ingredient. In the last couple of years, I've removed most pre-made cleaning and hygiene products from my life. I make my own shampoo, deodorant, toilet cleaner, kitchen cleaner, and I also make my own toothpaste.<br /><br />Chances are you already have all of the ingredients at home to make it. The recipe consists of <a href='baking_soda.html'>baking soda</a>, <a href='coconut_oil.html'>coconut oil</a> and <a href='peppermint_oil.html'>peppermint oil</a> (also food grade).<br /><br />You can also brush your teeth with a simple baking soda and water paste. Baking soda is abrasive enough to remove accumulations on teeth and rinses completely clear with only a very slightly salty taste. For those who dislike brushing with a salty taste, adding coconut and peppermint oil helps to smooth down both the taste and texture.<br /><br /><b>NOTE</b>: In colder climates your toothpaste will solidify and scraping some onto your toothbrush can be a challenge. Put the jar near a heater for 5 minutes or so to help soften it down.<br /><br />Toothpaste is only as good as your brushing (which should last for a min of 2min). It is more important to brush your teeth thoroughly than to use toothpaste, especially after eating sweets.<br /><br />");
RecipePart basic_toothpaste_toothpaste = create_part("toothpaste");
add_instruction(&basic_toothpaste_toothpaste, "Mix <i>22 g (1 1/2 tbsp)</i> of <a href='baking_soda.html'>baking soda</a> with <i>45 ml</i> of <a href='coconut_oil.html'>coconut oil</a>. If the coconut oil is hard, melt it down and mix it with the dry ingredients");
add_instruction(&basic_toothpaste_toothpaste, "Add <i>2.5 ml (1/2 tsp)</i> of food grade <a href='peppermint_oil.html'>peppermint oil</a>.");
add_instruction(&basic_toothpaste_toothpaste, "Mix everything up and brush your teeth!");
add_serving(&basic_toothpaste_toothpaste, &baking_soda, "22 g");
add_serving(&basic_toothpaste_toothpaste, &coconut_oil, "45 ml");
add_serving(&basic_toothpaste_toothpaste, &peppermint_oil, "2.5 ml");
add_part(&basic_toothpaste, &basic_toothpaste_toothpaste);

// beer bread
Recipe beer_bread = create_recipe("beer bread", bread, "1 loaf", 20160920, 60);
set_description(&beer_bread, "<b>Recipe location:</b> Sidney, BC. Canada.<br /><br />The idea of making beer bread came from a book written by Lin Pardey called \"The Care and Feeding of the Offshore Crew\". In this book, Lin talks about cooking at sea and has an entire chapter dedicated to baking onboard. In this chapter, the authot talks about the many ways to make fresh bread while sailing, including a quickbread recipe that uses 3 basic ingredients: flour, sugar and beer.<br /><br />Beer bread you say? Right up my alley! The next day I gave it a try, the result is fantastic — surprising given the little effort it takes to make it.<br /><br />The best thing about this bread, is that it can taste different everytime. Using different beer, will change the taste and color of the bread. I tried baking with an IPA (21st amendment), a Hefeweizen (Sunriver brewing co) and a brown ale (Hobgoblin).<br /><br />If you have self-rising flour, you can omit the baking powder and salt. You can use even less ingredients if you have a craft beer that has live yeast sediment at the bottom of the bottle. If you have a beer like that only flour, sugar and beer will do. Have fun experimenting with beers in your bread!<br /><br />We made a video of the beer bread-making process, check it out <a href='https://www.youtube.com/watch?v=ppASJRVcXmM' target='_blank'>here</a>.<br /><br />");
RecipePart beer_bread_beer_bread = create_part("beer bread");
add_instruction(&beer_bread_beer_bread, "Preheat your oven at <u>180 °C (350 °F)</u>.");
add_instruction(&beer_bread_beer_bread, "In a large bowl, mix <i>240 g (2 cups)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>, <i>2.5 g (1/2 tsp)</i> of <a href='salt.html'>salt</a>, <i>7 g (1 1/2 tsp)</i> of <a href='baking_powder.html'>baking powder</a>, <i>45 g (3 tbsp)</i> of <a href='granulated_sugar.html'>sugar</a> and <i>330 ml (1 can)</i> of <a href='beer.html'>beer</a>. Mix well, and add extra flour if the dough is too sticky — no kneading is required.");
add_instruction(&beer_bread_beer_bread, "Coat the bottom of your bread pan with <i>15 ml (1 tbsp)</i> of <a href='canola_oil.html'>vegetable oil</a>, make sure to spread it out evenly, and thinly. Sprinkle <i>~20 g (2 tbsp)</i> of <a href='corn_meal.html'>corn meal</a> in your bread pan, or tray, shake the cornmeal around so it covers the entire bottom.");
add_instruction(&beer_bread_beer_bread, "Transfer the bread dough into the pan. Sprinkle <i>30 g (~3 tbsp)</i> of <a href='oats.html'>oats</a> on top of the bread, press them down into the dough lightly.");
add_instruction(&beer_bread_beer_bread, "Bake for <u>1 hour</u>.");
add_instruction(&beer_bread_beer_bread, "Let cool on a dish towel, or rack.");
add_serving(&beer_bread_beer_bread, &all_purpose_flour, "240 g");
add_serving(&beer_bread_beer_bread, &salt, "1.25 g");
add_serving(&beer_bread_beer_bread, &baking_powder, "7 g");
add_serving(&beer_bread_beer_bread, &granulated_sugar, "45 g");
add_serving(&beer_bread_beer_bread, &beer, "330 ml (1 can)");
add_serving(&beer_bread_beer_bread, &cornmeal, "20 g");
add_serving(&beer_bread_beer_bread, &canola_oil, "15 ml");
add_serving(&beer_bread_beer_bread, &rolled_oats, "30 g");
add_part(&beer_bread, &beer_bread_beer_bread);

// borscht with tofu sour cream
Recipe borscht_with_tofu_sour_cream = create_recipe("borscht with tofu sour cream", sidedish, "4 quarts", 20141116, 60);
set_description(&borscht_with_tofu_sour_cream, "Devine has been taking russian classes every week, and he comes back from his lessons with new words to teach me. Last week, he not only returned with new words, he also brought back a Borscht recipe.<br /><br />Borscht is traditionally served with sour cream. A lot of vegan sour cream recipes have cashews, because of my tree nut allergy, i had to opt for something different. soft tofu does the job well, the mix of that plus lemon and apple cider vinegar gives a perfect sour taste!<br /><br />");
RecipePart borscht_with_tofu_sour_cream_sour_cream = create_part("sour cream");
add_instruction(&borscht_with_tofu_sour_cream_sour_cream, "Drain <i>349g (1 pack)</i> of <a href='soft_tofu.html'>soft tofu</a>, press it lightly with a cloth or paper towels to remove some of the water.");
add_instruction(&borscht_with_tofu_sour_cream_sour_cream, "Put the <a href='tofu.html'>tofu</a>, <i>5 g (1 tsp)</i> of <a href='whole_cane_sugar.html'>whole cane sugar</a>, <i>15 ml (1 tbsp)</i> of <a href='apple_cider_vinegar.html'>apple cider vinegar</a> and <i>25 ml (5 tsp)</i> of <a href='lemon_juice.html'>lemon juice</a> in a bowl. Blend until smooth.");
add_instruction(&borscht_with_tofu_sour_cream_sour_cream, "Put in the refrigerator for <u>1 hour</u> to give it time to thicken.");
add_serving(&borscht_with_tofu_sour_cream_sour_cream, &soft_tofu, "349 g");
add_serving(&borscht_with_tofu_sour_cream_sour_cream, &whole_cane_sugar, "5 g");
add_serving(&borscht_with_tofu_sour_cream_sour_cream, &apple_cider_vinegar, "15 ml");
add_serving(&borscht_with_tofu_sour_cream_sour_cream, &lemon_juice, "5 tsp");
add_part(&borscht_with_tofu_sour_cream, &borscht_with_tofu_sour_cream_sour_cream);
RecipePart borscht_with_tofu_sour_cream_borscht = create_part("borscht");
add_instruction(&borscht_with_tofu_sour_cream_borscht, "Bring <i>950 ml (4 cups)</i> of <a href='vegetable_bouillon.html'>vegetable bouillon</a> to a boil, add <i>2 peeled medium-sized</i> <a href='red_beets.html'>red beets</a>. Boil until softened.");
add_instruction(&borscht_with_tofu_sour_cream_borscht, "Peel and slice <i>1 medium-sized</i> <a href='sweet_potato.html'>sweet potato</a>. Add to pot and let boil for <u>15 minutes</u>.");
add_instruction(&borscht_with_tofu_sour_cream_borscht, "While the potatoes are cooking, peel and julienne <i>2 carrots</i>, cut <i>1/2</i> a <a href='red_onion.html'>red onion</a> and mince <i>3 cloves</i> of <a href='garlic.html'>garlic</a>. Add it all to a pan and fry until fragrant. Near the end add <i>30 g (2 tbsp)</i> of <a href='tomato_paste.html'>tomato paste</a>, then toss in the pot.");
add_instruction(&borscht_with_tofu_sour_cream_borscht, "Cut <i>1/2 head</i> of a small <a href='red_cabbage.html'>red cabbage</a> into thin strips, add to pot.");
add_instruction(&borscht_with_tofu_sour_cream_borscht, "Put <i>1</i> <a href='bay_leaf.html'>bay leaf</a>, season with <a href='salt.html'>salt</a> and <a href='pepper.html'>pepper</a> and let it simmer for <u>10 minutes</u> or until all the veggies are nice and soft.");
add_instruction(&borscht_with_tofu_sour_cream_borscht, "At the end, take pot off heat and stir in <i>10 ml (2 tsp)</i> of <a href='lemon_juice.html'>lemon juice</a>. Remove the <a href='bay_leaf.html'>bay leaf</a>." );
add_instruction(&borscht_with_tofu_sour_cream_borscht, "You can serve as is if you like a chunkier soup, otherwise purée it with a handblender. Serve hot with tofu sour cream! For thicker and tastier Borscht, cook the soup the day before you plan to eat it. Let it cool and refrigerate it. It tastes better the next day, just re-heat it. Trust me, it'll be delicious.");
add_serving(&borscht_with_tofu_sour_cream_borscht, &vegetable_bouillon, "950 ml");
add_serving(&borscht_with_tofu_sour_cream_borscht, &red_beets, "2");
add_serving(&borscht_with_tofu_sour_cream_borscht, &sweet_potatoes, "1");
add_serving(&borscht_with_tofu_sour_cream_borscht, &carrots, "2");
add_serving(&borscht_with_tofu_sour_cream_borscht, &red_onion, "1/2");
add_serving(&borscht_with_tofu_sour_cream_borscht, &garlic, "3");
add_serving(&borscht_with_tofu_sour_cream_borscht, &tomato_paste, "30 g");
add_serving(&borscht_with_tofu_sour_cream_borscht, &red_cabbage, "1.5, medium");
add_serving(&borscht_with_tofu_sour_cream_borscht, &bay_leaf, "1");
add_serving(&borscht_with_tofu_sour_cream_borscht, &sea_salt, "2.5 g");
add_serving(&borscht_with_tofu_sour_cream_borscht, &black_pepper, "2.5 g");
add_serving(&borscht_with_tofu_sour_cream_borscht, &lemon_juice, "10 ml");
add_part(&borscht_with_tofu_sour_cream, &borscht_with_tofu_sour_cream_borscht);

// breadfruit gnocchi
Recipe breadfruit_gnocchi = create_recipe("breadfruit gnocchi", pasta, "4 people", 20171610, 90);
set_description(&breadfruit_gnocchi, "<b>Recipe location:</b> Vava'u, Tonga<br /><br />We are about to leave Tonga, the tropics and the land of bountiful <a href='breadfruit.html'>breadfruit</a>. This versatile fruit can be cooked into fries, eaten with <a href='coconut_milk.html'>coconut milk</a>, made into chips, or like this recipe suggests, it can be made into gnocchi.<br /><br />Breadfruit has a taste and texture that resembles that of <a href='potato.html'>potato</a>, and so it makes sense that it too can be made into gnocchi. The flesh of the fruit can be kneaded with ease, especially if the fruit is very ripe. I have tried to knead it when half-ripe, it works too, but requires added moisture and more kneading - not to mention that it doesn't have as much flavor. Ripe breadfruit develops a sweet taste. It can be difficult to catch it at the right moment, like avocados they have a tendency to overripen overnight. Because we like it so much, we've bought many and have had time to better tell when it can be eaten. The outside becomes soft to the touch, but only just.<br /><br />We serve these with a light sauce, to better taste the gnocchi. A sauce that we enjoy, is minced garlic and chili peppers cooked in olive oil. The sauce is poured overtop and sprinkled with bits of shredded nori.<br /><br />");
RecipePart breadfruit_gnocchi_gnocchi = create_part("gnocchi");
add_instruction(&breadfruit_gnocchi_gnocchi, "Wait until your <a href='breadfruit.html'>breadfruit</a> is very ripe, soft to the touch. A softer breadfruit will be easier to knead, and will taste sweeter.");
add_instruction(&breadfruit_gnocchi_gnocchi, "Preheat oven to <u>200 °C (425 °F)</u>. Rub outside of breadfruit with <a href='olive_oil.html'>olive oil</a>, and wrap with aluminium foil. Cook for <u>1 hour</u>.");
add_instruction(&breadfruit_gnocchi_gnocchi, "Remove foil, peel skin away, cut in half, and remove the seed.");
add_instruction(&breadfruit_gnocchi_gnocchi, "Let cool, and mash with your hands into a cohesive dough. Add <a href='flour.html'>flour</a>, <i>30 g (1/4 cup)</i> at a time, until the <a href='breadfruit.html'>breadfruit</a> flesh stops sticking to your fingers. You may need more, or less, depending on the size of your fruit.");
add_instruction(&breadfruit_gnocchi_gnocchi, "Sprinkle your work surface with <a href='flour.html'>flour</a>, take a golf-sized ball of dough, and roll it into a thin, finger-sized log. Cut the log into bite-sized pieces (around 2cm long) and repeat for the rest of the dough.");
add_instruction(&breadfruit_gnocchi_gnocchi, "At this point, you can choose to freeze the gnocchi, or to prepare them straight away. To cook them, bring a pot of <a href='water.html'>water</a> to a boil, add the gnocchi, and cook for <u>5 minutes</u> or so, or <i>until they start to rise to the surface</i>.");
add_instruction(&breadfruit_gnocchi_gnocchi, "Serve with a light sauce, to better taste the subtle, but sweet flavor of the breadfruit gnocchi.");
add_serving(&breadfruit_gnocchi_gnocchi, &breadfruit, "1 small");
add_serving(&breadfruit_gnocchi_gnocchi, &olive_oil, "5 ml");
add_serving(&breadfruit_gnocchi_gnocchi, &all_purpose_flour, "150 g");
add_part(&breadfruit_gnocchi, &breadfruit_gnocchi_gnocchi);

// breadfruit pasta
Recipe breadfruit_pasta = create_recipe("breadfruit pasta", pasta, "4 people", 20181110, 20);
set_description(&breadfruit_pasta, "<b>Recipe location:</b> Suva, Fiji.<br /><br />Yes, another <a href='breadfruit.html'>breadfruit</a> recipe! This fruit is one of the most versatile ingredients I've ever cooked with, it's cheap and pairs well with just about anything. When we returned to the south pacific last june, you can be sure that the first thing we went looking for at the market was this lovely green wonder. It's become a staple for us, a treat and food we are excited to cook and eat.<br /><br />While in a grocery store in Fiji, we spotted breadfruit flour! A company called <b><a href='http://friendfiji.com' target='_blank'>Friend's Fiji style</a></b> sells it in bags of 300g. It's a good alternative if the fresh kind can't be found. It's something we'll stock up on when we leave, so we continue to have breadfruit in our diet (in some form).<br /><br /><img src='../media/recipes/breadfruit_pasta_1.jpg'/><br /><br />Making pasta from scratch requires your hands, a knife and a rolling pin (or bottle, whatever works). Making pasta by hand that is even and thin is a challenge, it's easier to opt for thicker 'udon-style' noodles. Expert soba chefs in Japan can cut noodles thinly, but this requires experienced hands. We have a good blade, but lack the patience as well as the desire to cut even noodles. We're very okay with imperfect noodles.<br /><br /><img src='../media/recipes/breadfruit_pasta_2.jpg'/><br /><br /><b>Tricks for cutting pasta evenly:</b> Roll the flattened piece of dough and cutting it cross-wise is the key, the details on how to do this are in the recipe instructions below.<br /><br />If you come to Fiji and like to make pasta from scratch, try and find some breadfruit flour. That same company also produces cassava flour.<br /><br /><img src='../media/recipes/breadfruit_pasta_3.jpg'/><br /><br />We like to eat breadfruit pasta with garlic, chilis and bitter melon, sauteed in olive oil with some shredded nori on top- simple, and tasty. Alternatively, to make regular pasta, just sub the breadfruit flour for <a href='whole_wheat_flour.html'>whole wheat flour</a> or <a href='spelt_flour.html'>spelt flour</a>.<br /><br />");
RecipePart breadfruit_pasta_breadfruit_pasta = create_part("breadfruit pasta");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "In a large bowl, mix <i>90g (3/4 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a> with <i>80g (3/4 cup)</i> of <a href='breadfruit_flour.html'>breadfruit flour</a>.");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Add <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>, mix well.");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Add <i>180 ml (3/4 cup)</i> of <a href='water.html'>water</a>, mix until it starts to clump up, then knead with your hands until you have a smooth dough. Let rest for <u>20 mins</u> (this will make it easier to roll).");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Separate dough in two, put one half aside. Separating the dough makes it easier to roll if you have limited counter space (like I do).");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Sprinkle flour on your working surface, flatten the ball out from the center to the outer edge. Continue to roll, flipping the dough over once or twice and dusting it with flour to prevent it sticking. Roll to desired thickness.");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Fold the sheet of dough into a flat roll, then cut into it cross-wise into 0.5cm strips.");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Repeat all the steps for the other half of the dough.");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Carefully unroll each coil with your fingers, then transfer to a floured surface.");
add_instruction(&breadfruit_pasta_breadfruit_pasta, "Bring water to a boil. Add pasta. Cook for a minute or so, and serve with a light sauce!");
add_serving(&breadfruit_pasta_breadfruit_pasta, &breadfruit_flour, "80 g");
add_serving(&breadfruit_pasta_breadfruit_pasta, &all_purpose_flour, "90 g");
add_serving(&breadfruit_pasta_breadfruit_pasta, &salt, "1.25 g");
add_serving(&breadfruit_pasta_breadfruit_pasta, &water, "180 ml");
add_part(&breadfruit_pasta, &breadfruit_pasta_breadfruit_pasta);

// buckwheat tea
Recipe buckwheat_tea = create_recipe("buckwheat tea", sidedish, "1 teapot", 20191206, 15);
set_description(&buckwheat_tea, "A friend from Japan gave us some soba cha <b>そば茶</b> last month, it's something we've had before but that we never thought of making ourselves. It's a type of tea that is served in some soba shops, as a companion drink to buckwheat noodles. It has a subtle nutty flavor, and is perfect to drink after dinner or later at night as it doesn't have any caffeine.<br /><br /><img src='../media/recipes/buckwheat_tea_1.jpg'><br /><br /> I like to roast the groats as I need them, but it's possible to prepare a larger batch ahead of time. The roasted buckwheat groats will store for a few months if kept in a cool and dry place. <br /><br /><b>Roasting a larger batch:</b> When roasting a larger batch, use a larger pan so that the groats don't sit atop of each other. The groats must be in a single layer so they can cook evenly.<br /><br /> <b>Re-using the softened groats:</b> It's possible to re-use the softened groats by pouring more water onto them, although the flavor will not be as strong. If you like to minimize food waste, it's possible to incorporate the soft groats in other meals. For example, you can mix it into rice, cookies, breads etc.<br /><br /> <b>Cooking groats using oven:</b> You can roast your buckwheat groats in your oven. Roast them at 180 °C (350 °F) for 50 minutes, while stirring on occasion.");
RecipePart buckwheat_tea_roasting = create_part("roasting");
add_instruction(&buckwheat_tea_roasting, "Bring a pan to medium heat, add <i>30 g (2 tbsp)</i> of <a href='buckwheat_groats'>raw buckwheat groats</a>. ");
add_instruction(&buckwheat_tea_roasting, "Toast the buckwheat_groats until they're fragrant and lightly browned, all while stirring constantly. Do this for about <u>5 minutes</u>. Then, lower the heat and continue to toast the groats until they turn a deep brown color (~5 minutes, depending on the temperature of the pan). Continue stirring, and watch them so they don't burn. ");
add_instruction(&buckwheat_tea_roasting, "Transfer to a bowl, let cool. ");
add_serving(&buckwheat_tea_roasting, &buckwheat_groats, "30 g");
add_part(&buckwheat_tea, &buckwheat_tea_roasting);
RecipePart buckwheat_tea_infusion = create_part("infusion");
add_instruction(&buckwheat_tea_infusion, "Add <i>30 g (2 tbsp)</i> of <a href='buckwheat_groats'>roasted buckwheat groats</a> into the tea strainer of a teapot, and pour <i>800 ml (3 cups)</i> of hot water overtop. Let tea infuse for <u>5-10 minutes</u>.");
add_instruction(&buckwheat_tea_infusion, "The used buckwheat groats can be re-used in other meals. You can add it to cooked rice, pasta, or in baked goods. I like to use it to replace the oats in <a href='crackers.html'>this cracker</a> recipe.");
add_serving(&buckwheat_tea_infusion, &buckwheat_groats, "30 g, roasted");
add_serving(&buckwheat_tea_infusion, &water, "800 ml, hot");
add_part(&buckwheat_tea, &buckwheat_tea_infusion);

// chickpea salad sandwich
Recipe chickpea_salad_sandwich = create_recipe("chickpea salad sandwich", dinner, "4 portions", 20160118, 20);
set_description(&chickpea_salad_sandwich, "<b>Recipe location:</b> Sidney, BC. Canada.<br /><br />I recently ordered ingredients from BC Kelp, a company in northern Canada that grows their own seaweed. A lot of the food I make these days has either nori, wakame, bull kelp or bladderwack whole tips in it.<br /><br />I'd like to see more people cooking with sea vegetables. You can snack on dried seaweed, or add it to soups and salads. The powdered version I'm using in this recipe, adds a lot of umami and color to meals.<br /><br />You can order your own bull kelp powder from the <a href='http://www.bckelp.com/index.html' target='_blank'>BC Kelp website</a>, they have a wide range of quality seaweed to choose from.<br /><br /><b>Recommendations</b><br /><br />Adding a bay leaf, a bit of onion or a clove or two of garlic to the cooking water add a subtle seasoning to the chickpeas and boost flavor.<br /><br /><b>Substitutions</b><br /><br />If you don't have access to <a href='bull_kelp_powder.html'>bull kelp powder</a>, you can use 5 ml capers or dulse flakes. These needs to be added to create a briny no-tuna taste.<br /><br />");
RecipePart chickpea_salad_sandwich_chickpea_salad = create_part("chickpea salad");
add_instruction(&chickpea_salad_sandwich_chickpea_salad, "For veganaise, see the <a href='veganaise.html'>recipe</a>.");
add_instruction(&chickpea_salad_sandwich_chickpea_salad, "Cook <i>125 g (3/4 cup)</i> of dried <a href='chickpeas.html'>chickpeas</a> (see <a href='#chickpeas.html'>instructions</a>), or use 1 can (15oz). Mash chickpeas down with a fork.");
add_instruction(&chickpea_salad_sandwich_chickpea_salad, "Mix the mashed <a href='chickpeas.html'>chickpeas</a> with <i>60 g (3-4 tbsp)</i> of <a href='veganaise.html'>venagaise</a>, <i>15 g (1 tsp)</i> <a href='bull_kelp_powder.html'>bull kelp powder</a>, <i>2 chopped</i> <a href='pickled_cucumbers.html'>pickles</a> and <i>1.25 g (1/4 tsp)</i> of <a href='cayenne_pepper_powder.html'>cayenne pepper powder</a>.");
add_instruction(&chickpea_salad_sandwich_chickpea_salad, "Season with <a href='salt.html'>salt</a> and <a href='black_pepper.html'>black pepper</a> and serve over toasted bread with tomatoes, salad etc.");
add_serving(&chickpea_salad_sandwich_chickpea_salad, &chickpeas, "250 g, cooked");
add_serving(&chickpea_salad_sandwich_chickpea_salad, &prepared_veganaise, "60 g");
add_serving(&chickpea_salad_sandwich_chickpea_salad, &bull_kelp_powder, "5 g");
add_serving(&chickpea_salad_sandwich_chickpea_salad, &pickled_cucumbers, "2");
add_serving(&chickpea_salad_sandwich_chickpea_salad, &cayenne_pepper_powder, "1.25 g");
add_part(&chickpea_salad_sandwich, &chickpea_salad_sandwich_chickpea_salad);

// chocolate chip cookies
Recipe chocolate_chip_cookies = create_recipe("chocolate chip cookies", basic, "12", 20191206, 20);
set_description(&chocolate_chip_cookies, "I've been making chocolate chips cookies for a while, but never thought it worthy of a recipe because there are so many online already. In the interest of posting absolute basic recipes though, I felt it deserved to be written down, especially because cookie chemistry is difficult. Slight differences in moisture content, altitude, fat content and sugar content can alter the look and texture of a cookie. There are reasons for the quantities and choice of each ingredient, all interact with each other to give cookies their sweet and soft texture.<br /><br />This recipe is a good base. Substitutions are possible, but changing ingredients — depending on the ingredient — can mess up your recipe. In my suggestions I give examples of good substitutions, and how to modify the recipe to get a good result.<b class='head'>Substitutions</b> <b>Flour:</b> If you choose to use spelt flour, a more nutritious alternative, add 5 ml (5 ml) of baking powder to help it rise.<br /><br /><b>Fat:</b> The fat in cookies is a big part of their structure. I've added vegan butter as is behaves like actual butter, it helps create baked goods that are more tender by shortening gluten strands. When fat coats flour, it slows down the process of gluten formation creating a more tender product <a href='https://bakerbettie.com/function-of-butter-in-baking/#The_Function_of_Butter_in_Baking'>ref</a>. It's possible to make your own vegan butter, check out this <a href='https://www.forkandbeans.com/2015/07/11/vegan-butter-substitute'>recipe by fork and beans</a>.<br /><br /><b>Sugar:</b> Sugar is important in cookies, it helps with the flavor, color and texture. The oven temperature causes the sugar to react with the proteins, this is what gives baked goods their brown color. Brown sugar is important in this recipe, as it adds moisture to the dough. If you use 100% granulated sugar the cookie won't spread as well, not unless you add more moisture. You can sub granulated sugar with whole cane sugar or coconut sugar.<br /><br /><b>Add-ins:</b> If you have a sensitivity to caffeine use <a href='carob_chips'>carob chips</a> instead of chocolate chips. Switching to carob won't affect the cookies. <b class='head'>Troubleshooting</b>\"I followed the recipe, but my cookies don't look the same as yours!\" The quirkiness of different ovens makes it difficult to give accurate cooking times. Having a thermometer in your oven is the best way to read the temperature accurately.<br /><br /><b>Help! Cooking spreading too much!</b> If your cookie is spreading too much, you may have added too much sugar. Sugar is hygroscopic, it absorbs liquid but once it bakes it releases that and if there's too much, then it keeps spreading. Oven temperature is another factor. The hotter the oven, the more quickly the fat melts before the cookies have time to set. Depending on your oven, you may need to bake cookies longer but at a lower temperatures.<br /><br /><b>Help! Cookie not spreading!</b> One of the most common reasons why cookies don't spread is because there's too much flour in the dough. Try using less, and consider increasing the brown sugar by a few tablespoons. This will add slightly more moisture and help the cookies spread. Be sure you're also using room temperature ingredients, especially butter, to promote the best spread and texture.<br /><br />Read more about <a href='https://www.kingarthurflour.com/blog/2016/03/14/cookie-chemistry-2'>cookie chemistry</a>. Recipe inspired from <a href='https://www.kingarthurflour.com/recipes/chocolate-chip-cookies-recipe'>this one</a>. ");
RecipePart cookie_mix = create_part("cookie mix");
add_instruction(&cookie_mix, "Measure <i>113 g (1/2 cup)</i> of <a href='vegan_butter.html'>vegan butter</a>. It should be slightly cooler than room temp.");
add_instruction(&cookie_mix, "Heat oven to <u>190 °C (375 °F)</u>.");
add_instruction(&cookie_mix, "In a small bowl, mix <i>15 g (1 tbsp)</i> of <a href='flax_seeds'>ground flax seeds</a> with <i>45 ml (3 tbsp)</i> of <a href='water'>water</a>. This is your flax 'egg', let it thicken for <u>5 minutes</u>.");
add_instruction(&cookie_mix, "Mix <i>125 g (1 cup)</i> of <a href='all_purpose_flour'>all purpose flour</a> with <i>2.5 g (1/2 tsp)</i> of <a href='baking_soda'>baking soda</a> and <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>.");
add_instruction(&cookie_mix, "In a bowl, cream <i>75 g (6 tbsp)</i> of <a href='granulated_sugar'>granulated sugar</a>, <i>90 g (6 tbsp)</i> of <a href='brown_sugar'>brown sugar</a> with <i>113 g (1/2 cup)</i> of <a href='vegan_butter'>vegan butter</a> until well incorporated. Then add <i>45 ml (3 tbsp)</i> of <a href='soy_milk.html'>soy milk</a>, <i>2.5 ml (1/2 tsp)</i> of <a href='apple_cider_vinegar.html'>apple cider vinegar</a> and the flax egg. Mix again until smooth and creamy.");
add_instruction(&cookie_mix, "Add the wet ingredients to the dry, and mix well (but don't over do it).");
add_instruction(&cookie_mix, "Add <i>170 g (1 cup)</i> of <a href='chocolate_chips.html'>chocolate chips</a>, distribute evenly.");
add_instruction(&cookie_mix, "Scoop up generous balls of dough and lay on a baking sheet, leave plenty of room inbetween. ");
add_instruction(&cookie_mix, "Bake cookies until they start to brown and crisp at the edges, depending on your oven this may take <u>10-15 minutes</u>. My oven is old and bakes at lower temperatures so I need to bake them for the longer time. See notes in above description.");
add_serving(&cookie_mix, &all_purpose_flour, "125 g");
add_serving(&cookie_mix, &baking_soda, "2.5 g");
add_serving(&cookie_mix, &salt, "1.25 g");
add_serving(&cookie_mix, &granulated_sugar, "75 g");
add_serving(&cookie_mix, &brown_sugar, "92 g");
add_serving(&cookie_mix, &vegan_butter, "113 g");
add_serving(&cookie_mix, &soy_milk, "45 ml");
add_serving(&cookie_mix, &apple_cider_vinegar, "2.5 ml");
add_serving(&cookie_mix, &flax_seeds, "15 g, ground");
add_serving(&cookie_mix, &water, "45 ml");
add_serving(&cookie_mix, &chocolate_chips, "170 g");
add_part(&chocolate_chip_cookies, &cookie_mix);

// coffee jelly
Recipe coffee_jelly = create_recipe("coffee jelly", dessert, "2 servings", 20190225, 40);
set_description(&coffee_jelly, "Coffee jelly is a popular dessert and drink (you could say) that is served in coffee shops in Japan. It is very simple to make, it doesn't require refrigeration to become jelly and it is delicious!<br /><br />The key ingredient in this recipe is kanten, or agar agar, a gelling agent that is algae based and that is widely available in asia. It is available in powder form, and also in sheets. Sheets can be melted in a liquid the same way as the powder form. Agar agar has no calories, and imparts no flavour.<br /><br />You can use instant coffee as well for this recipe, using 30 ml of instant coffee per cup.<br /><br />On Pino, we sometimes make the mix without a sweetener, and pour a spoonful on top afterwards.<br /><br /><b>Quick soy pudding</b><br /><br />You can eat the coffee jelly as is, or you can add the cubes in another recipe like a coconut or soy milk pudding. It can also be used as a garnish for other desserts.<br /><br />Also, I sometimes make a quick 'soy pudding' by heating some soy milk in a pan with some arrowroot starch. The mixture will thicken, it becomes pudding-like and is delicious over coffee jelly.<br /><br />");
RecipePart coffee_jelly_jelly = create_part("jelly");
add_instruction(&coffee_jelly_jelly, "Brew a pot of <a href='coffee.html'>coffee</a>.");
add_instruction(&coffee_jelly_jelly, "Pour 350ml of coffee into a saucepan, add <i>30 ml</i> of <a href='maple_syrup.html'>maple syrup</a> and bring to a boil,");
add_instruction(&coffee_jelly_jelly, "Lower heat, then add <i>4 g</i> (around <i>10 ml</i>) of <a href='agar_agar_powder.html'>agar agar powder</a>. Stir constantly for <u>2 minutes</u>.");
add_instruction(&coffee_jelly_jelly, "Pour the mixture into two small bowls, or cups. Let stand for <u>30 minutes and up to 1 hour</u>. It doesn't need to be refrigerated to solidify, but you can do that if you prefer it cold.");
add_serving(&coffee_jelly_jelly, &coffee, "350 ml");
add_serving(&coffee_jelly_jelly, &agar_agar_powder, "4 g");
add_serving(&coffee_jelly_jelly, &maple_syrup, "30 ml");
add_part(&coffee_jelly, &coffee_jelly_jelly);

// corn dumplings
Recipe corn_dumplings = create_recipe("corn dumplings", sidedish, "2 people", 20171408, 40);
set_description(&corn_dumplings, "<b>Recipe location:</b> Alofi, Niue.<br /><br />I found an old vegetarian cookbook in a book-sharing shelf. I must have spent an hour reading through it, the recipes were fantastic, the title read \"The Farm Vegetarian Cookbook\". The cookbook had an entire section dedicated to cooking with corn. It's in there that I found a recipe for masa dumplings.<br /><br />Masa is corn that is simmered and ground into a paste and is the base for many recipes. You can make Mexican-style tortillas with it, that, or you can use it to make dumplings! I don't have access to fresh corn, nor do I have the space or the time to make my own masa, but I used <a href='corn_semolina.html'>corn semolina</a> instead and it worked! The only difference is that you need to add boiling water so you can roll the mixture into balls. If you were to use masa, you wouldn't need the added moisture.<br /><br />The texture of the dumplings is fun and chewy. The outside is soft, but the inside is like that of dense cornbread.<br /><br />Recommendations</b><br /><br />Devine & I enjoy eating it with tomato sauce or with a spicy apricot sauce. Instead of apricot jam, you can add apricot juice to the mix (orange will taste wonderful too). I added jam because I didn't have any juice on the boat, and besides, it works well in this recipe.<br /><br />Adding extra spices, like cumin, chili flakes and turmeric will add a nice flavour to the dumpling mix.<br /><br />");
RecipePart corn_dumplings_dumplings = create_part("dumplings");
add_instruction(&corn_dumplings_dumplings, "Mix <i>160g (1 cup)</i> <a href='corn_semolina.html'>corn semolina</a>, <i>30 g (1/4 cup)</i> <a href='all_purpose_flour.html'>all purpose flour</a>, <i>2.5 g (1/2 tsp)</i> <a href='baking_soda.html'>baking soda</a>, <i>5 g (1 tsp)</i> <a href='baking_powder.html'>baking powder</a>, and <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>. Stir until well mixed.");
add_instruction(&corn_dumplings_dumplings, "Bring <i>240 ml (1 cup)</i> of <a href='water.html'>water</a> to a boil, pour into dry ingredients and mix well. When the dough is wet and sticky, form <b>roughly 13mm balls</b> with your hands. You can make larger or smaller balls - note that cooking time will change if the diameter is bigger.");
add_instruction(&corn_dumplings_dumplings, "Bring a big pot of water to a boil, add corn balls and boil for <u>10-15 minutes</u>.");
add_instruction(&corn_dumplings_dumplings, "Drain, let cool.");
add_serving(&corn_dumplings_dumplings, &corn_semolina, "160 g");
add_serving(&corn_dumplings_dumplings, &all_purpose_flour, "30 g");
add_serving(&corn_dumplings_dumplings, &baking_soda, "2.5 g");
add_serving(&corn_dumplings_dumplings, &baking_powder, "5 g");
add_serving(&corn_dumplings_dumplings, &salt, "1.25 g");
add_serving(&corn_dumplings_dumplings, &water, "250 ml, boiled");
add_part(&corn_dumplings, &corn_dumplings_dumplings);
RecipePart corn_dumplings_sauce = create_part("sauce");
add_instruction(&corn_dumplings_sauce, "Mix all sauce ingredients together (all, except the arrowroot starch).");
add_instruction(&corn_dumplings_sauce, "Put <i>5 g (1 tsp)</i> of <a href='arrowroot_starch.html'>arrowroot starch</a> in a bowl, and dissolve in <i>60 ml (1/4 cup)</i> of <a href='water.html'>water</a>. Set aside.");
add_instruction(&corn_dumplings_sauce, "Pour sauce into a pan, and bring to medium heat. Once the sauce starts to boil, add a few spoonfuls of the arrowroot starch mix to thicken the sauce. Once thickened, divide corn dumplings into bowls and pour sauce overtop.");
add_serving(&corn_dumplings_sauce, &apricot_jam, "15 g");
add_serving(&corn_dumplings_sauce, &soy_sauce, "30 ml");
add_serving(&corn_dumplings_sauce, &japanese_rice_vinegar, "30 ml");
add_serving(&corn_dumplings_sauce, &lemon_juice, "15 ml");
add_serving(&corn_dumplings_sauce, &sesame_oil, "5 ml");
add_serving(&corn_dumplings_sauce, &water, "60 ml");
add_serving(&corn_dumplings_sauce, &chili_pepper_flakes, "15 g");
add_serving(&corn_dumplings_sauce, &ginger_root, "5 ml");
add_serving(&corn_dumplings_sauce, &garlic, "2, minced");
add_serving(&corn_dumplings_sauce, &arrowroot_starch, "5 g");
add_part(&corn_dumplings, &corn_dumplings_sauce);

// crackers
Recipe crackers = create_recipe("crackers", basic, "40 crackers", 20181229, 25);
set_description(&crackers, "<b>Recipe location:</b> Majuro, Marshall Islands.<br /><br />Making crackers is simple, everyone should know how to make them. There are no downsides to knowing, and in a bind it's a useful skill, but not just this, making your own produces less waste and you control what you ingest. What's great about a basic recipe is that you can swap ingredients with little consequence (most times) and you can add to it to suit your needs and cravings.<br /><br />Devine & I love to eat crackers as snacks, usually between breakfast and lunch, a cracker with some peanut butter to quiet our stomachs. We also like to eat <a href='https://grimgrains.com/#papaya+bruschetta+topping' target='_blank'>papaya salsa</a> with crackers, it makes a good scooping vessel for the fruit.<br /><br />You can make these crackers without extras with just the 'cracker' portion of the recipe. They're just as delicious that way, but you can add seeds and spices for added flavour and nutrition. I like to add seeds to mine, like <a href='pumpkin_seeds.html'>pumpkin seeds</a>, <a href='flax_seeds.html'>flax seeds</a>, <a href='sesame_seeds.html'>sesame seeds</a>, or <a href='sunflower_seeds.html'>sunflower seeds</a>. I recommend adding <a href='black_pepper.html'>black pepper</a>, or <a href='chili_pepper_flakes.html'>chili pepper flakes</a>, these are also delicious if you sprinkle some salt over the top of them.<br /><br />In this recipe, I recommend using a mortar and pestle to grind the rolled oats, but a food processor will also work. You can use another sweetener - I used whole cane sugar because it's what's available to me at the moment, but otherwise I'd opt for <a href='maple_syrup.html'>maple syrup</a>. I prefer to use a baking mat (I have a copper one) than parchment paper, since it's reusable and easy to clean.<br /><br />");
RecipePart crackers_crackers = create_part("crackers");
add_instruction(&crackers_crackers, "Preheat oven to <u>200 °C (400 °F)</u>.");
add_instruction(&crackers_crackers, "In a mortar, grind <i>40 g (1/4 cup)</i> of <a href='rolled_oats.html'>rolled oats</a> into a fine powder.");
add_instruction(&crackers_crackers, "Mix the powdered <a href='rolled_oats.html'>rolled oats</a> (rough-ish grind is fine) with <i>90 g (3/4 cup)</i> of <a href='spelt_flour.html'>spelt flour</a>. Add <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>, <i>15 g (1 tbsp)</i> of <a href='whole_cane_sugar.html'>whole cane sugar</a> as well as <i>25 ml (1 1/2 tbsp)</i> of <a href='olive_oil.html'>olive oil</a>.");
add_instruction(&crackers_crackers, "If you want to add extras, add them now, and stir well.");
add_instruction(&crackers_crackers, "Add <i>60 ml (1/4 cup)</i> of <a href='water.html'>water</a>. Add an <i>extra 15 ml (1 tbsp)</i> of water (sparingly!) if more moisture is needed and knead into a smooth ball.");
add_instruction(&crackers_crackers, "Split dough in two. Set your first ball of dough onto a baking mat, or parchment paper, and roll the dough into a <i>thin (~3mm) sheet</i> with a rolling pin.");
add_instruction(&crackers_crackers, "<i>Score the dough</i> into cracker-sized squares, so they will be easier to separate afterwards. Repeat for the second ball of dough.");
add_instruction(&crackers_crackers, "Bake for <u>10-12 minutes</u>, keeping an eye on them to make sure they don't burn. Let cool, and enjoy!");
add_serving(&crackers_crackers, &rolled_oats, "40 g, ground");
add_serving(&crackers_crackers, &spelt_flour, "90 g");
add_serving(&crackers_crackers, &salt, "1.25 g");
add_serving(&crackers_crackers, &whole_cane_sugar, "15 g");
add_serving(&crackers_crackers, &olive_oil, "25 ml");
add_serving(&crackers_crackers, &water, "60 ml");
add_part(&crackers, &crackers_crackers);
RecipePart crackers_extras = create_part("extras");
add_instruction(&crackers_extras, "For more bulk, add pumpkin seeds, black sesame seeds and flax seeds!");
add_serving(&crackers_extras, &pumpkin_seeds, "30 g");
add_serving(&crackers_extras, &black_sesame_seeds, "15 g");
add_serving(&crackers_extras, &flax_seeds, "15 g, ground");
add_part(&crackers, &crackers_extras);

// dark gyoza
Recipe dark_gyoza = create_recipe("dark gyoza", sidedish, "40 wrappers", 20141217, 60);
set_description(&dark_gyoza, "We made homemade gyoza dough with some friends a few weeks back, and it took FOREVER. Devine had the amazing idea of using our pasta maker to do it. We still needed to do a bit of kneading, to get it through the machine the first time. After that, it's easy and sweat-free!<br /><br /><img src='../media/recipes/dark_gyoza_1.jpg'/><br /><br />We didn't have any round cookie cutters, the last time we tried I was using upside down glasses. Didn't work well because the rims aren't sharp and smooshes the dough down instead. Again, Devine had a stroke of genius: Cans! I had an empty chickpea pan lying around, it was about the size of a gyoza wrapper so we used that to poke holes through the dough.<br /><br /><img src='../media/recipes/dark_gyoza_2.jpg'/><br /><br />I know not everyone has a pasta maker, you don't need one to make wrappers. You can just use a rolling pin.<br /><br /><img src='../media/recipes/dark_gyoza_4.jpg'><br /><br />Gyoza wrapper techniques and ratios were based on the recipe from <a href='http://www.justonecookbook.com/recipes/gyoza-wrappers/' target='_blank'>Just one cookbook</a>. She explains it really well too on her blog it's worth taking a look. I learned a lot from her even if our techniques differ slightly. While I preferred not to knead by hand, or with a rolling pin, i did do it her way the first time.<br /><br />");
RecipePart dark_gyoza_dough = create_part("dough");
add_instruction(&dark_gyoza_dough, "Mix <i>240 g (2 cups)</i> of <a href='all_purpose_flour.html'>all purpose flour</a> with <i>5 g (1 tsp)</i> of <a href='bamboo_charcoal_powder.html'>bamboo charcoal powder</a> in a bowl.");
add_instruction(&dark_gyoza_dough, "Dissolve <i>2.5 g (1/2 tsp)</i> of <a href='salt.html'>salt</a> in <i>120 ml (1/2 cup)</i> of <a href='just-boiled_water.html'>just-boiled water</a>, and pour into the flour little by little while mixing.");
add_instruction(&dark_gyoza_dough, "Knead the dough on a clean flat surface for a few minutes until it becomes smooth. If your dough is too dry, add 15 ml (1 tbsp) of hot water and repeat as needed.");
add_instruction(&dark_gyoza_dough, "<i>Separate the dough in 3</i>. Roll into balls and wrap them up with a bag or place in a lidded bowl, refrigerate for at least <u>30 minutes</u>. Leaving it to rest in the refrigerator makes the dough easier to manipulate afterwards.");
add_instruction(&dark_gyoza_dough, "Flatten each piece out with a rolling pin into a rough rectangle, thin enough so that you can run it through the pasta maker. Pass it through your pasta maker a few times, incrementing down to the thinnest setting gradually. You can use a rolling pin instead, it works but it takes a lot more effort. Both techniques work, this one just happens to be quicker and easier on your body. Note that depending on your pasta maker, the dough will come out a bit thicker than a traditional gyoza. If you want it thinner you can flatten it a bit more with a rolling pin.");
add_instruction(&dark_gyoza_dough, "Put the thin sheet of dough onto a clean flat surface. Take a can (with about a 7.6 cm diameter) and start to poke holes into it, these are your gyoza wrappers! You can use cookie cutters if you have some. I used an old chickpea can I had.");
add_instruction(&dark_gyoza_dough, "Pile the wrappers, sprinkle some <a href='cornstarch.html'>cornstarch</a> (or potato starch) inbetween each piece so they don't stick together. Put a damp towel over your wrappers so they don't dry out. Repeat this process for the other 2 portions of dough, and you can re-use the scraps and make new dough to run through the pasta maker.");
add_serving(&dark_gyoza_dough, &all_purpose_flour, "240 g");
add_serving(&dark_gyoza_dough, &bamboo_charcoal_powder, "5 g");
add_serving(&dark_gyoza_dough, &salt, "5 g");
add_serving(&dark_gyoza_dough, &water, "120 ml");
add_serving(&dark_gyoza_dough, &cornstarch, "15 g");
add_part(&dark_gyoza, &dark_gyoza_dough);
RecipePart dark_gyoza_filling = create_part("filling");
add_instruction(&dark_gyoza_filling, "Cut stems off of <i>3</i> of dried <a href='shiitake.html'>shiitake mushrooms</a>, rehydrate by pouring boiling water over them. Keep them submerged for <u>30 min or up to 1 hour</u>. It's better to rehydrate them <u>overnight (minimum of 8H)</u>. If you do this, the flavour will be stronger, but if you're in a rush, boiling water is fine. If you have fresh shiitake, you can also skip this step.");
add_instruction(&dark_gyoza_filling, "Cut <a href='scallions.html'>scallions</a> and <a href='shiitake.html'>shiitake mushrooms</a> into small pieces. Do the same for the <a href='green_cabbage.html'>green cabbage</a>, but make sure to remove the core of the cabbage leaves first. Add to a big bowl.");
add_instruction(&dark_gyoza_filling, "Grate <i>227 g 91/2 pack</i> of firm tofu and <i>2</i> <a href='carrots.html'>carrots</a>. Add to the bowl.");
add_instruction(&dark_gyoza_filling, "Finally, add <i>2 cloves</i> of minced <a href='garlic.html'>garlic</a> and <i>2.5 cm (1 inch)</i> of grated <a href='ginger_root.html'>ginger root</a>, <i>5 ml (1 tsp)</i> of <a href='soy_sauce.html'>soy sauce</a>, <i>5 ml (1 tsp)</i> of <a href='sesame_oil.html'>sesame oil</a>, <i>5 ml (1 tsp)</i> of <a href='sake.html'>sake</a>, <a href='black_pepper.html'>black pepper</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> to the bowl. Knead bowl contents with your hands until the mixture is well incorporated.");
add_serving(&dark_gyoza_filling, &tofu, "227 g, grated"); 
add_serving(&dark_gyoza_filling, &shiitake, "3");
add_serving(&dark_gyoza_filling, &green_cabbage, "140 g");
add_serving(&dark_gyoza_filling, &carrots, "2, grated");
add_serving(&dark_gyoza_filling, &garlic, "2 cloves, minced");
add_serving(&dark_gyoza_filling, &ginger_root, "2.5 cm, grated");
add_serving(&dark_gyoza_filling, &soy_sauce, "5 ml");
add_serving(&dark_gyoza_filling, &sesame_oil, "5 ml");
add_serving(&dark_gyoza_filling, &sake, "5 ml");
add_serving(&dark_gyoza_filling, &black_pepper, "1.25 g");
add_serving(&dark_gyoza_filling, &salt, "1.25 g");
add_part(&dark_gyoza, &dark_gyoza_filling);
RecipePart dark_gyoza_dipping_sauce = create_part("dipping sauce");
add_instruction(&dark_gyoza_dipping_sauce, "Combine <i>45 ml (3 tbsp)</i> of <a href='Japanese_rice_vinegar.html'>Japanese rice vinegar</a> with <i>45 ml (3 tbsp)</i> of <a href='soy_sauce.html'>soy sauce</a>, <i>5 ml (1 tsp)</i> of <a href='sesame_oil.html'>sesame oil</a> and <i>5 g (1 tsp)</i> of <a href='chili_pepper_flakes.html'>dried chili flakes</a>.");
add_serving(&dark_gyoza_dipping_sauce, &japanese_rice_vinegar, "45 ml");
add_serving(&dark_gyoza_dipping_sauce, &soy_sauce, "45 ml");
add_serving(&dark_gyoza_dipping_sauce, &sesame_oil, "5 ml");
add_serving(&dark_gyoza_dipping_sauce, &chili_pepper_flakes, "5 g");
add_part(&dark_gyoza, &dark_gyoza_dipping_sauce);
RecipePart dark_gyoza_pan_fry = create_part("pan fry");
add_instruction(&dark_gyoza_pan_fry, "Take 1 gyoza wrapper, wet all around the edge with water using your fingers. Keep a bowl of water close to dip your fingers in.");
add_instruction(&dark_gyoza_pan_fry, "Put a spoonful of filling in the middle.");
add_instruction(&dark_gyoza_pan_fry, "Close it. Make little folds with the flap that is facing you using both hands, leaving the back part smooth. Make sure it's sealed tight. Repeat for the rest of the gyoza skins, alternate between fillings. Cover with a damp towel while you work so they don't dry up.");
add_instruction(&dark_gyoza_pan_fry, "Put some vegetable oil in a pan on medium heat. Add gyoza in 2 rows of 3 in the pan. Cook until bottom becomes golden.");
add_instruction(&dark_gyoza_pan_fry, "Add <i>80 ml (1/3 cup)</i> of <a href='water.html'>water</a> and put a lid on. Let steam until all the water evaporates.");
add_instruction(&dark_gyoza_pan_fry, "Add a bit of <a href='sesame_oil.html'>sesame oil</a> and cook until crispy.");
add_serving(&dark_gyoza_pan_fry, &water, "80 ml");
add_serving(&dark_gyoza_pan_fry, &sesame_oil, "5 ml");
add_part(&dark_gyoza, &dark_gyoza_pan_fry);

// fresh pesto pasta
Recipe fresh_pesto_pasta = create_recipe("fresh pesto pasta", pasta, "2 servings", 20150922, 20);
set_description(&fresh_pesto_pasta, "Summer is over, temperature in Montreal started to dip into the lower 20's. Colder times means collecting the remaining fresh herbs from my balcony garden. This year, I'm happy to say that my plants have thrived.<br /><br />My basil plant was a real beauty, I harvested all I could from it and made pesto. With it, I made this recipe.<br /><br />The combination of carrots and zucchinis look beautiful on a plate. To complete the dish, I added some roasted pumpkin seeds, and topped it off with cherry tomatoes. For bulk, I added cavatappi pasta to this dish. Cavatappi pasta (or scoobi-doo) happens to be my favorite pasta cut (I'm a sucker for shapes)<br /><br />See how to roast your own <a href='roasted_pumpkin_seeds.html'>pumpkin seeds</a>.<br /><br />");
RecipePart fresh_pesto_pasta_pesto = create_part("pesto");
add_instruction(&fresh_pesto_pasta_pesto, "Blend the following ingredients in a food processor or blender: <i>~400 g (2 cups)</i> of <a href='fresh_basil.html'>fresh basil</a>, <i>2 cloves</i> of <a href='garlic.html'>garlic</a>, <i>10 g (1/4 cup)</i> of <a href='nutritional_yeast.html'>nutritional yeast</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> and a dash of <a href='black_pepper.html'>black pepper</a>. For a cheezier pesto, add an extra 25 g (1/4 cup) of <a href='quick_sunflower_seed_parmesan'>sunflower seed parmesan</a>.");
add_instruction(&fresh_pesto_pasta_pesto, "Add <i>60 ml (1/4 cup)</i> of <a href='olive_oil.html'>olive oil</a> and blend again until smooth. Set aside.");
add_serving(&fresh_pesto_pasta_pesto, &basil, "400 g");
add_serving(&fresh_pesto_pasta_pesto, &garlic, "2 cloves");
add_serving(&fresh_pesto_pasta_pesto, &olive_oil, "60 ml");
add_serving(&fresh_pesto_pasta_pesto, &nutritional_yeast, "10 g");
add_part(&fresh_pesto_pasta, &fresh_pesto_pasta_pesto);
RecipePart fresh_pesto_pasta_main = create_part("main");
add_instruction(&fresh_pesto_pasta_main, "Bring a pot of <a href='water.html'>water</a> to a boil, add <i>150 g (1 1/2 cups)</i> of <a href='cavatappi.html'>cavatappi</a> pasta and cook until tender. While the pasta is cooking julienne <i>2</i> <a href='carrots.html'>carrots</a> and <i>1</i> <a href='zucchini.html'>zucchini</a>.");
add_instruction(&fresh_pesto_pasta_main, "Drizzle some <a href='olive_oil.html'>olive oil</a> in a pan and bring up to medium heat. Add the vegetable strips and cook for <u>2-3 minutes</u>.");
add_instruction(&fresh_pesto_pasta_main, "Remove from heat, add the cooked pasta as well as a few tablespoons of pesto! The recipe makes about 1 cup of pesto, which you can keep and use for future meals.");
add_instruction(&fresh_pesto_pasta_main, "Season pasta with <a href='salt.html'>salt</a> and <a href='black_pepper.html'>black pepper</a>. Finally, top it all off with <a href='roasted_pumpkin_seeds.html'>roasted pumpkin seeds</a> and <a href='small_heirloom_tomatoes.html'>small heirloom tomatoes</a>!");
add_serving(&fresh_pesto_pasta_main, &carrots, "2");
add_serving(&fresh_pesto_pasta_main, &cavatappi, "150 g");
add_serving(&fresh_pesto_pasta_main, &zucchini, "1 large");
add_serving(&fresh_pesto_pasta_main, &small_heirloom_tomatoes, "6");
add_part(&fresh_pesto_pasta, &fresh_pesto_pasta_main);

// veganaise
Recipe veganaise = create_recipe("veganaise", sauce, "1.5 cup", 20191206, 5);
set_description(&veganaise, "<b>Recipe location:</b> Montreal, QC. Canada<br /><br />Making mayo is easy. This is a basic recipe that doesn't take long to prepare, and that requires few tools.<br /><br />It's possible to add other spices and ingredients to augment the flavor, like smoked paprika, garlic or fresh herbs. This recipe lends well to substitutions:<br /><br /><b>Substitutions</b><br /><br /><b>Oil :</b> It's possible to use other neutral oils like sunflower oil, but I wouldn't recommend using olive oil as it will impart too much flavor.<br /><br /><b>Traditional mayo :</b> This recipe uses less oil than the average mayo recipe. I used soft tofu to have a thicker mixture to avoid adding extra oil, for a richer veganaise you can use soy milk or other plant milks. To do this, mix your ingredients in the order listed in the recipe (switching the tofu for 250 ml or 1 cup plant milk), then pour 350 ml to 595 ml (1 1/2 to 2 1/2 cups) of oil in a slow, and steady stream with the blender running. The mayo will thicken after ~4-5 minutes of blending.<br /><br /><b>Mustard : </b>Instead of using <a href='dijon_mustard.html'>prepared mustard</a>, you can sub with 5 g (1 tsp) of mustard powder. If you have a spice grinder or a coffee grinder, you can grind your own mustard powder from whole mustard seeds. In this recipe, I used old style <a href='dijon_mustard.html'>dijon mustard</a> with mustard seeds, which explains the texture present in the veganaise. If you want to know how to make old-style dijon mustard, check out <a href='#mustard_from_seed.html'>my recipe</a>.<br /><br /><b>Sweetener : </b>If you prefer sweeter venagaise, add 5 g (1 tsp) of a sweetener.<br /><br /><b>Vinegar :</b> Substitute lemon for apple cider vinegar, using equal amounts.");
RecipePart veganaise_veganaise = create_part("veganaise");
add_instruction(&veganaise_veganaise, "Place the <i>250 g (1 cup)</i> of <a href='soft_tofu.html'>soft tofu</a>, <i>15 ml (1 tbsp)</i> <a href='apple_cider_vinegar.html'>apple cider vinegar</a>, <i>30 g (2 tbsp)</i> <a href='dijon_mustard.html'>dijon mustard</a> and <i>1.25 g (1/4 tsp)</i> <a href='salt.html'>salt</a> in a blender and process until smooth. Add <i>60 ml (4 tbsp)</i> of <a href='canola_oil.html'>canola oil</a>, and continue to blend for <u>1-2 minutes</ul>.");
add_serving(&veganaise_veganaise, &soft_tofu, "250 g");
add_serving(&veganaise_veganaise, &canola_oil, "60 ml");
add_serving(&veganaise_veganaise, &apple_cider_vinegar, "15 ml");
add_serving(&veganaise_veganaise, &dijon_mustard, "30 g");
add_serving(&veganaise_veganaise, &salt, "1.25 g");
add_part(&veganaise, &veganaise_veganaise);

// hop ice cream
Recipe hop_ice_cream = create_recipe("hop ice cream", dessert, "1 L", 20150628, 600);
set_description(&hop_ice_cream, "Me and Devine like craft beer a lot. To help satisfy our evergrowing craving, I decided to try and make hop flavoured ice cream.<br /><br />This ice cream is definitely for people who love the smell and taste of hops (as well as cold summer dairy-free treats).<br /><br /><img src='../media/recipes/hop_ice_cream_2.jpg'/><br /><br />I wasn't sure how to 'infuse' the hops into the ice cream base, we tried making hop tea by infusing it overnight in the fridge, but the taste wasn't strong enough. Felt we would get better results and better flavour if the hops were heated. The pouch method worked pretty well, the ice cream base was perfect! Full of delicious bitterness! Devine suggested to make a concentrate with the hops first, and then mix it into the base next time.<br /><br />The hops used in this recipe are an American variety called \"Colombus\". We got them from <a href='http://www.choppeabarrock.com' target='_blank'>La Choppe a Barrock</a> on Villeneuve and Coloniale in Montreal. Every time we make home brews, we pick up the ingredients there! You can buy a wide variety of hop buds there, you can even get the pellet kind.<br /><br />It was my second time using the ice cream maker, I got it from the 'Free stuff Montreal' group on facebook. It looks like a little red pail — super cute. The woman I got it from even had the manual! You can make ice cream without a machine, although having one helps with the churning process. It saves a lot of time and effort. If i hadn't gotten that ice cream maker for free, i dont think id have one now, but since it's here might as well use it!<br /><br />If you too, are having too many beers these days, try and cook with hops instead. Same taste, different format!<br /><br />");
RecipePart hop_ice_cream_hop_ice_cream = create_part("hop ice cream");
add_instruction(&hop_ice_cream_hop_ice_cream, "Roughly chop 7 hop buds (may want to use less if you want it less hoppy). Place leaves into the center of a cheese cloth and make a little loose pouch with it, tying the end. Reserve for later.");
add_instruction(&hop_ice_cream_hop_ice_cream, "Shake <i>two 378ml cans</i> of <a href='coconut_milk.html'>coconut milk</a> before opening them, full-fat coconut milk separates over time and shaking will help to mix it up again. Reserve a <i>120 ml (1/2 cup)</i> of <a href='coconut_milk.html'>coconut milk</a> for later, put the rest in a pan with <i>120 ml (1/2 cup)</i> of <a href='maple_syrup.html'>maple syrup</a> and <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>.");
add_instruction(&hop_ice_cream_hop_ice_cream, "Bring up to medium-low heat, stir until the <a href='maple_syrup.html'>maple syrup</a> has dissolved completely.");
add_instruction(&hop_ice_cream_hop_ice_cream, "Stir <i>22 g (1 1/2 tbsp)</i> of <a href='cornstarch.html'>cornstarch</a> into the <i>120 ml (1/2 cup)</i> of <a href='coconut_milk.html'>coconut milk</a> that was set aside. When dissolved, pour into the pan with the rest of the coconut milk, while whisking slowly.");
add_instruction(&hop_ice_cream_hop_ice_cream, "Increase the heat to medium and add your hop pouch! Let the mixture cook for about <u>8 minutes</u>. Don't let it come to a boil! Reduce the heat if necessary. Move the pouch around once in a while, pinching it down lightly with a wooden spoon to get some of that nice hop flavour out into your ice cream base.");
add_instruction(&hop_ice_cream_hop_ice_cream, "Remove from heat, stir in <i>5 ml (1 tsp)</i> of <a href='vanilla_extract.html'>vanilla extract</a>. Leave it to cool — don't remove the hop pouch!");
add_instruction(&hop_ice_cream_hop_ice_cream, "When cool, take the pouch out and squeeze out all of the 'hop juice' into the pan using your hands. Mix one last time and pour into a container. Place a sheet of parchment paper over the top so you don't get a skin forming on the surface. Refrigerate for a minimum of <u>4 hours</u> (or overnight!).");
add_instruction(&hop_ice_cream_hop_ice_cream, "Pull your ice cream out of the fridge. It should have a thick pudding-like texture. Pour into your ice cream maker and churn it to desired consistency (I let it run for <u>~23 minutes</u>).");
add_instruction(&hop_ice_cream_hop_ice_cream, "If you don't have an ice cream maker, please refer to any these <a href='http://www.thekitchn.com/how-to-make-ice-cream-without-an-ice-cream-machine-171060' target='_blank'>6 methods</a> from the Kitchn.");
add_instruction(&hop_ice_cream_hop_ice_cream, "Transfer to a container and press a sheet of parchment paper over the top to keep ice crystals from forming. Your ice cream should be ready to eat in <u>5 hours</u> or so! If you want harder ice cream wait until the next day to eat it. Enjoy!");
add_serving(&hop_ice_cream_hop_ice_cream, &hops, "7 buds");
add_serving(&hop_ice_cream_hop_ice_cream, &coconut_milk, "2 X 378ml cans");
add_serving(&hop_ice_cream_hop_ice_cream, &maple_syrup, "120 ml");
add_serving(&hop_ice_cream_hop_ice_cream, &sea_salt, "1.25 g");
add_serving(&hop_ice_cream_hop_ice_cream, &cornstarch, "22 g");
add_serving(&hop_ice_cream_hop_ice_cream, &vanilla_extract, "5 ml");
add_part(&hop_ice_cream, &hop_ice_cream_hop_ice_cream);

// houjicha overnight oatmeal
Recipe houjicha_overnight_oatmeal = create_recipe("houjicha overnight oatmeal", breakfast, "1 serving", 20140904, 10);
set_description(&houjicha_overnight_oatmeal, "Mornings are made better with the nutty and earthy flavour of houjicha oatmeal. If you're not familiar with houjicha, visit a tea shop and ask for it, it's a Japanese roasted tea that is often served with sweet and savoury dishes. It's a good low-caffeine alternative to most teas, and adds just the right amount of flavour to oatmeal without overpowering it.<br /><br />");
RecipePart houjicha_overnight_oatmeal_oatmeal = create_part("oatmeal");
add_instruction(&houjicha_overnight_oatmeal_oatmeal, "Boil <i>240 ml (1 cup)</i> of <a href='water.html'>water</a>, then infuse <i>10 g (2 tsp)</i> of <a href='houjicha_leaves.html'>houjicha leaves</a> in a tea strainer. You can leave it anywhere from <u>1-3 min</u>, it depends how strong you like your tea to be. The longer you brew houjicha the nuttier it'll taste.");
add_instruction(&houjicha_overnight_oatmeal_oatmeal, "Remove the tea leaves and let cool.");
add_instruction(&houjicha_overnight_oatmeal_oatmeal, "Add <i>1</i> chopped <a href='banana.html'>banana</a> (to cut the bitterness of the tea), as well as <i>15g (1 tbsp)</i> of ground <a href='flax_seeds.html'>flax seeds</a> (for added nutrition), blend until smooth.");
add_instruction(&houjicha_overnight_oatmeal_oatmeal, "Mix in <i>45 g (~1/3 cup)</i> of <a href='rolled_oats.html'>rolled oats</a>, stir well, and then put it in the fridge overnight. Enjoy for breakfast the next day!");
add_serving(&houjicha_overnight_oatmeal_oatmeal, &water, "240 ml");
add_serving(&houjicha_overnight_oatmeal_oatmeal, &houjicha, "10 g");
add_serving(&houjicha_overnight_oatmeal_oatmeal, &banana, "1");
add_serving(&houjicha_overnight_oatmeal_oatmeal, &flax_seeds, "15 g, ground");
add_serving(&houjicha_overnight_oatmeal_oatmeal, &rolled_oats, "45 g");
add_part(&houjicha_overnight_oatmeal, &houjicha_overnight_oatmeal_oatmeal);

// halloween pumpkin cookies
Recipe halloween_pumpkin_cookies = create_recipe("halloween pumpkin cookies", dessert, "15 cookies", 20151010, 30);
set_description(&halloween_pumpkin_cookies, "Been a while since I've baked cookies. It being pumpkin season, the choice was obvious - pumpkin cookies it is.<br /><br /><img src='../media/recipes/halloween_pumpkin_cookies_2.jpg'/><br /><br />These are not very sweet, namely because of the addition of coconut sugar which has a subtle and more complex taste. Coconut sugar will make your cookies a darker shade of orange, almost brown, and I think it fits nicely with the toned-down color of the pumpkin seeds. If you want a bold orange color, use white cane sugar instead.<br /><br /><b>Left over chocolate:</b> This recipe makes more chocolate than is needed for the recipe. If you want to keep it for later, pour it in a chocolate mold or shallow container and put it in the freezer. It will become solid and you'll have delicious homemade chocolate on the side for later times! Once it's solid, pop it out of the mold and put it in a closed container. Keep it in the freezer until you're ready to eat it!<br /><br />Pumpkin puree how-to: Remove seeds and stringy flesh from pumpkin. Cut the pumpkin into large chunks and place on a baking sheet. Preheat oven at 180 °C (350 ºF). Roast pumpkin for ~45 minutes until pumpkin is soft and tender. Let cool. Remove skin, and process into a food processor or use a handstick blender. If pumpkin puree is too wet, strain liquid through cheese cloth, if too dry, add a bit of water. The puree keeps for 3 days in the fridge, and up to 3 months frozen.");
RecipePart halloween_pumpkin_cookies_cookie_dough = create_part("cookie dough");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "Preheat oven to <u>180 °C (350 °F)</u>.");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "Stir <i>120 g (1/2 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>, <i>120 g (1/2 cup)</i> of <a href='spelt_flour.html'>spelt flour</a> and <i>7 g (1 1/2 tsp)</i> of <a href='baking_powder.html'>baking powder</a> together in a bowl.");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "See above description to make your own pumpkin puree.");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "In another bowl, add <i>122g (1/2 cup)</i> of pureed <a href='pumpkin.html'>pumpkin</a>, <i>(3 tbsp) 45 ml</i> of <a href='canola_oil.html'>canola oil</a> and <i>70 g (1/2 cup)</i> of <a href='coconut_sugar.html'>coconut sugar</a>. Stir everything together until smooth and well incorporated.");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "Add the wet to the dry ingredients, mix with a wooden spoon until you get a soft and consistent dough. It will be sticky, but that's fine, if it's too sticky add extra flour. Let the dough rest for <u>10 minutes</u>.");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "Line a baking sheet with a baking mat. Make little balls of dough, as round as you can make them, and flatten each one gently. Bake for <u>15 minutes</u>.");
add_instruction(&halloween_pumpkin_cookies_cookie_dough, "Decorate each cookie with <i>3</i> <a href='pumpkin_seeds.html'>pumpkin seeds</a>, sticking them at the top (pointy end in).");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &all_purpose_flour, "120 g");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &spelt_flour, "120 g");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &baking_powder, "7 g");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &coconut_sugar, "70 g");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &canola_oil, "45 ml");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &pumpkin, "122 g, pureed");
add_serving(&halloween_pumpkin_cookies_cookie_dough, &pumpkin_seeds, "45 seeds");
add_part(&halloween_pumpkin_cookies, &halloween_pumpkin_cookies_cookie_dough);
RecipePart halloween_pumpkin_cookies_chocolate = create_part("chocolate");
add_instruction(&halloween_pumpkin_cookies_chocolate, "Mix <i>60 ml (1/4 cup)</i> of <a href='canola_oil.html'>canola oil</a>, <i>27 g (1/4 cup)</i> of <a href='cocoa_powder.html'>cocoa powder</a>, <i>1.25 ml (1/4 tsp)</i> <a href='vanilla_extract.html'>vanilla extract</a>, <i>30 ml (2 tbsp)</i> <a href='maple_syrup.html'>maple syrup</a> and a pinch of <a href='salt.html'>salt</a> together in a glass bowl.");
add_instruction(&halloween_pumpkin_cookies_chocolate, "Put a bit of <a href='water.html'>water</a> to boil in a pot (large enough to fit your bowl, or to have it hanging overtop).");
add_instruction(&halloween_pumpkin_cookies_chocolate, "Lower the heat to a simmer, add the glass bowl with the chocolate into the pot, stir with a spoon until the chocolate is smooth and runny.");
add_instruction(&halloween_pumpkin_cookies_chocolate, "Remove from heat. Take a toothpick and dip into the chocolate, draw pumpkin faces on your cookies and serve as is!");
add_serving(&halloween_pumpkin_cookies_chocolate, &canola_oil, "60 ml");
add_serving(&halloween_pumpkin_cookies_chocolate, &cocoa_powder, "27 g");
add_serving(&halloween_pumpkin_cookies_chocolate, &vanilla_extract, "1.25 ml");
add_serving(&halloween_pumpkin_cookies_chocolate, &maple_syrup, "30 ml");
add_part(&halloween_pumpkin_cookies, &halloween_pumpkin_cookies_chocolate);

// lentils with roasted beet sauce
Recipe lentils_with_roasted_beet_sauce = create_recipe("lentils with roasted beet sauce", dinner, "2 servings", 20150202, 60);
set_description(&lentils_with_roasted_beet_sauce, "This recipe is especially good and filling with lentils, but it's also possible to serve it on other grains or even pasta. I used beluga lentils, because they hold their shape better than <a href='brown_lentils.html'>brown lentils</a>.<br /><br />Beets make any dish beautiful, but it can stain your skin. Not to worry though - it doesn't stay, you'll just look like you've just killed something.<br /><br />Fresh peppermint is key in this recipe, don't omit it. If you have a fresh supply at home, that is perfect, otherwise store-bought stalks are fine. You can keep store-bought stalks longer if you do these simple steps: first, tear off any wilted leaves, wash the mint gently, and then put the stalks in a glass with a bit of water and a bag over the top. Then, store it in the refrigerator.<br /><br />");
RecipePart lentils_with_roasted_beet_sauce_lentils = create_part("lentils");
add_instruction(&lentils_with_roasted_beet_sauce_lentils, "Wash <i>115g (1/2 cup)</i> of dry <a href='beluga_lentils.html'>beluga lentils</a>. Put <i>375 ml (~1 1/2 cup)</i> of <a href='vegetable_bouillon.html'>vegetable bouillon</a> in a pot and add the <a href='lentils.html'>lentils</a>, bring to a boil.");
add_instruction(&lentils_with_roasted_beet_sauce_lentils, "Reduce heat to <u>medium</u> and simmer for <u>25 minutes</u>. Don't let them cook for too long or they'll get too mushy. If the lentils seem cooked but haven't absorbed all of the water, strain it out.");
add_serving(&lentils_with_roasted_beet_sauce_lentils, &beluga_lentils, "115 g");
add_serving(&lentils_with_roasted_beet_sauce_lentils, &vegetable_bouillon, "375 ml");
add_part(&lentils_with_roasted_beet_sauce, &lentils_with_roasted_beet_sauce_lentils);
RecipePart lentils_with_roasted_beet_sauce_sauce = create_part("sauce");
add_instruction(&lentils_with_roasted_beet_sauce_sauce, "Preheat oven to <u>200 °C (400 °F)</u>.");
add_instruction(&lentils_with_roasted_beet_sauce_sauce, "Peel and cut <i>3 medium-sized</i> <a href='red_beets.html'>red beets</a> into cubes. Toss with <i>5 ml (1 tsp)</i> of <a href='olive_oil.html'>olive oil</a>, <i>2.5 g (1/2 tsp)</i> of <a href='thyme.html'>thyme</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> and some <a href='black_pepper.html'>black pepper</a>.");
add_instruction(&lentils_with_roasted_beet_sauce_sauce, "Put chopped <a href='red_beets.html'>red beets</a> on a baking sheet and roast for <u>30 minutes</u>.");
add_instruction(&lentils_with_roasted_beet_sauce_sauce, "Cook <i>3 minced cloves</i> of <a href='garlic.html'>garlic</a> in a pan with a bit of <a href='olive_oil.html'>olive oil</a>, for a <u>2 minutes</u> at high heat. Transfer to a food processor along with <i>120 ml (1/2 cup)</i> of <a href='vegetable_bouillon.html'>vegetable bouillon</a>, <i>15 ml</i> of <a href='balsamic_vinegar.html'>balsamic vinegar</a>, <i>15 ml</i> <a href='lemon_juice.html'>lemon juice</a> and a <i>10 g (~1/4 cup)</i> of <a href='nutritional_yeast.html'>nutritional yeast</a>.");
add_instruction(&lentils_with_roasted_beet_sauce_sauce, "Once the red beets are ready, add them in with the rest and then purée everything until smooth.");
add_instruction(&lentils_with_roasted_beet_sauce_sauce, "Transfer the sauce to a pan, bring to medium heat and cook for <u>10 minutes</u>. Stir in <i>60 ml (1/4 cup)</i> of <a href='soy_milk.html'>soy milk</a>. Cook for an additional <u>5 minutes</u> and then serve over the lentils with <i>5 g</i> of chopped <a href='peppermint.html'>peppermint</a>.");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &red_beets, "3");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &olive_oil, "15 ml");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &thyme, "5 g");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &sea_salt, "1.25 g");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &black_pepper, "1.25 g");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &garlic, "3 cloves");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &vegetable_bouillon, "120 ml");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &balsamic_vinegar, "15 ml");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &lemon_juice, "15 ml");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &nutritional_yeast, "10 g");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &soy_milk, "60 ml");
add_serving(&lentils_with_roasted_beet_sauce_sauce, &peppermint, "5 g");
add_part(&lentils_with_roasted_beet_sauce, &lentils_with_roasted_beet_sauce_sauce);

// mason jar bread pudding
Recipe mason_jar_bread_pudding = create_recipe("mason jar bread pudding", dessert, "1 serving", 20150108, 30);
set_description(&mason_jar_bread_pudding, "Bread pudding was one of my favourite desserts when I was a kid. We would usually have some after the holidays, thanks to my aunt's crust-free sandwiches! She would usually just give the bag of crusts to my mom, which she in turn, used to make bread pudding.<br /><br /><img src='../media/recipes/mason_jar_bread_pudding_1.jpg'/><br /><br />I asked my mom for her personal bread pudding recipe, but she told me that it was best to just watch her do it. There aren't really any specific measurements, she just makes it from memory. That's the case with most of her recipes, most of them have never been written down.<br /><br />When she makes bread pudding, she makes a basin-full of it. My dad never has trouble going through all of it.<br /><br />As much as i'd like to make a bucketload of bread pudding at home, me and Devine would NEVER get through the whole thing. I don't like eating the same thing for a week, so I adapted my mom's bread pudding into a single-serving one. The mason jar is a wide-mouthed 1/2 pint jar.<br /><br />If ever you have some leftover crusts (even just a few) you can totally make this. It's a quick and simple dessert, with a taste of home.<br /><br />");
RecipePart mason_jar_bread_pudding_bread_pudding = create_part("bread pudding");
add_instruction(&mason_jar_bread_pudding_bread_pudding, "Preheat oven at <u>180 °C (350 °F)</u>");
add_instruction(&mason_jar_bread_pudding_bread_pudding, "Combine <i>90g (~1/4 cup)</i> of <a href='tofu.html'>soft tofu</a>, <i>80 ml (1/3 cup)</i> of <a href='soy_milk.html'>soy milk</a> and <i>1.25 ml (1/4 tsp)</i> of <a href='vanilla_extract.html'>vanilla extract</a>. Blend until smooth.");
add_instruction(&mason_jar_bread_pudding_bread_pudding, "Take a <i>250 ml (1/2 pint)</i> wide-mouthed mason jar. Fill it up halfway with <a href='bread_crusts.html'>bread crusts</a>. Cut <i>1 <a href='apple.html'>apple</a> into two</i>, then slice the half into tiny cubes, add to the jar. Then add the <a href='dried_raisins.html'>dried raisins</a>. Pour liquid to cover until it's level with the bread crusts");
add_instruction(&mason_jar_bread_pudding_bread_pudding, "Cover with more bread crusts and dried raisins. Pour more liquid onto it, until the jar is about 3/4 full.");
add_instruction(&mason_jar_bread_pudding_bread_pudding, "Sprinkle <i>5 g (1 tsp)</i> of <a href='brown_sugar.html'>brown sugar</a> on top, decorate with apple slices (use the other half of the <a href='apple.html'>apple</a>), add <i>1.25 g (1/4 tsp)</i>, <a href='cinnamon.html'>cinnamon</a> and <i>15 ml (1 tbsp)</i> of <a href='maple_syrup'>maple syrup</a> overtop.");
add_instruction(&mason_jar_bread_pudding_bread_pudding, "Cook for <u>20-25 minutes</u>, or until top is browned. Let cool, drizzle with some <a href='maple_syrup.html'>maple syrup</a> and enjoy!");
add_serving(&mason_jar_bread_pudding_bread_pudding, &soft_tofu, "90 g");
add_serving(&mason_jar_bread_pudding_bread_pudding, &soy_milk, "80 ml");
add_serving(&mason_jar_bread_pudding_bread_pudding, &vanilla_extract, "1.25 ml");
add_serving(&mason_jar_bread_pudding_bread_pudding, &bread_crusts, "20 g, packed");
add_serving(&mason_jar_bread_pudding_bread_pudding, &dried_raisins, "15 g");
add_serving(&mason_jar_bread_pudding_bread_pudding, &apple, "1, small");
add_serving(&mason_jar_bread_pudding_bread_pudding, &brown_sugar, "5 g");
add_serving(&mason_jar_bread_pudding_bread_pudding, &cinnamon, "1.25 g");
add_serving(&mason_jar_bread_pudding_bread_pudding, &maple_syrup, "15 ml");
add_part(&mason_jar_bread_pudding, &mason_jar_bread_pudding_bread_pudding);

// mushroom zucchini pasta
Recipe mushroom_zucchini_pasta = create_recipe("mushroom zucchini pasta", pasta, "4 servings", 20150303, 20);
set_description(&mushroom_zucchini_pasta, "As much as I love pasta, i like to vary my food a lot day to day. Once in a while, I like to make zucchini noodles! They're ready in an instant, and are delicious when topped with a two-mushroom velouté sauce.<br /><br />This meal is also green on green, most of the ingredients are different shades of the same colour. Fun.<br /><br />What other green things can I add to this? Wakame! I don't always have fresh greens in my fridge, when I don't, I use wakame. It's handy to have around, it keeps for a long time and doesn't require a lot of prep. Wakame is also a good source of <b>Iodine</b>.<br /><br />Edamame is another green food that I like. I have the frozen, de-shelled kind in my fridge. It saves a lot of time.<br /><br />Mushrooms aren't green but they do add a TON of flavour to sauces. Shiitake mushrooms have a lot of flavour, shimeji mushrooms have a subtle taste but look nice in a dish.<br /><br />");
RecipePart mushroom_zucchini_pasta_veggie_noodles_and_toppings = create_part("veggie noodles and toppings");
add_instruction(&mushroom_zucchini_pasta_veggie_noodles_and_toppings, "Rehydrate <i>30 g (2 tbsp)</i> of <a href='wakame.html'>wakame</a> in water, drain and set aside.");
add_instruction(&mushroom_zucchini_pasta_veggie_noodles_and_toppings, "Cut stems off of <i>5-6</i> of dried <a href='shiitake_mushrooms.html'>shiitake mushrooms</a>, rehydrate by pouring boiling water over them. Keep them submerged for <u>30 min</u>, or up to <u>1 hour</u>. It's better to rehydrate them overnight, for <u>a minimum of 8 hours</u>. If you do this, the flavour will be stronger, but if you're in a rush, boiling water is fine. If you have fresh <a href='shiitake.html'>shiitake</a>, you can also skip this step.");
add_instruction(&mushroom_zucchini_pasta_veggie_noodles_and_toppings, "Cut <i>4 medium-sized</i> <a href='zucchinis.html'>zucchinis</a> thinly using a julienne peeler or knife. Place in bowls. I don't always cook my zucchini, but if you prefer a warm version sauté them in a pan.");
add_serving(&mushroom_zucchini_pasta_veggie_noodles_and_toppings, &wakame, "30 g");
add_serving(&mushroom_zucchini_pasta_veggie_noodles_and_toppings, &shiitake, "5-6");
add_serving(&mushroom_zucchini_pasta_veggie_noodles_and_toppings, &zucchini, "4");
add_part(&mushroom_zucchini_pasta, &mushroom_zucchini_pasta_veggie_noodles_and_toppings);
RecipePart mushroom_zucchini_pasta_sauce = create_part("sauce");
add_instruction(&mushroom_zucchini_pasta_sauce, "Put <i>30 ml (2 tbsp)</i> of <a href='olive_oil.html'>olive oil</a> in a saucepan over medium heat, whisk in <i>30 g (2 tbsp)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>. Stir until it's completely absorbed by the oil. Let roux cook for a minute or so.");
add_instruction(&mushroom_zucchini_pasta_sauce, "Stir in <i>475 ml (2 cups)</i> of <a href='vegetable_bouillon.html'>vegetable bouillon</a>, bring heat up to a boil and then lower to a simmer. Add a <i>5-6</i> of <a href='shiitake_mushrooms.html'>shiitake mushrooms</a>, a <i>50 g</i> of <a href='shimeji_mushrooms.html'>shimeji mushrooms</a> and a <i>50 g (~1/3 cup)</i> of <a href='edamame.html'>shelled edamame</a> to the sauce.");
add_instruction(&mushroom_zucchini_pasta_sauce, "Let cook for <u>10 minutes</u>.");
add_instruction(&mushroom_zucchini_pasta_sauce, "When cooked, season sauce with salt and black pepper.");
add_instruction(&mushroom_zucchini_pasta_sauce, "Add the re-hydrated wakame and serve over zucchini pasta. Season with shijimi togarashi!");
add_serving(&mushroom_zucchini_pasta_sauce, &olive_oil, "30 ml");
add_serving(&mushroom_zucchini_pasta_sauce, &all_purpose_flour, "30 g");
add_serving(&mushroom_zucchini_pasta_sauce, &vegetable_bouillon, "475 molds");
add_serving(&mushroom_zucchini_pasta_sauce, &shimeji, "50 g");
add_serving(&mushroom_zucchini_pasta_sauce, &edamame, "50 g");
add_serving(&mushroom_zucchini_pasta_sauce, &shichimi_togarashi, "5 g");
add_part(&mushroom_zucchini_pasta, &mushroom_zucchini_pasta_sauce);

// mustard from seed
Recipe mustard_from_seed = create_recipe("mustard from seed", basic, "1 quarter pint (150ml)", 20181221, 20);
set_description(&mustard_from_seed, "<b>Recipe location:</b> Majuro, Marshall Islands.<br /><br />We always try our hardest to buy as few packaged foods as we can, and if we do buy pre-made goods we aim for glass, this too through isn't always possible.<br /><br />We ran out of mustard the other day, and couldn't find any at the store that wasn't packaged in plastic. We decided then that it was better to make our own. It requires few ingredients, little time to prepare (25min for prep, few days for soaking), and is inexpensive.<br /><br />First, you need some mustard seeds. You can choose between yellow, brown and black seeds, the color affects the 'heat' of the mustard. A dark seed imparts more flavour than the lighter variety, and is what we used in this recipe. The seeds are then soaked in a mixture of water and <a href='apple_cider_vinegar.html'>apple cider vinegar</a>, although you can also use beer and white wine as a base. The water should be room temperature or cold, because using hot liquids denatures the enzymes that create the 'heat' in mustard.<br /><br />We don't have <a href='tools.html'>tools</a> onboard that require electricity, so to grind and crush seeds and herbs we use a <b>heavy ceramic mortar and pestle</b>.<br /><br />After the mustard is ground, it is stored in a glass jar at room temperature. Because of its antibacterial properties, mustard <b>does not require refrigeration</b>.<br /><br /><img src='../media/recipes/mustard_from_seed_1.jpg'/><br /><br />The best thing about making it yourself is that you can vary the quantities and base ingredients according to your personal preferences. If you don't like having a strong vinegar taste, you can lessen the amount of apple cider vinegar and add more water instead. You can also add spices and sweeteners. For traditional 'yellow mustard', all you need to do is add turmeric (for color) and a sweetener.<br /><br />");
RecipePart mustard_from_seed_mustard = create_part("mustard");
add_instruction(&mustard_from_seed_mustard, "Soak <i>60 g (60ml)</i> of <a href='mustard_seeds.html'>mustard seeds</a> in a mixture of <i>45ml (3 tbsp)</i> of <a href='apple_cider_vinegar.html'>apple cider vinegar</a> and <i>20ml</i> of water.");
add_instruction(&mustard_from_seed_mustard, "Let soak for a few days, or until seeds have expanded 3 times their size (from 3 to 7 days). If the seeds have soaked up all of the liquids, add more water.");
add_instruction(&mustard_from_seed_mustard, "Drain the seeds from the liquids. Reserve liquids for later.");
add_instruction(&mustard_from_seed_mustard, "If you've got a blender or food processor, purée the seeds until smooth. Otherwise, add a small quantity of seeds in your mortar and grind them down with your pestle. Repeat until the seeds are crushed and puréed - the mixture doesn't need to be perfectly smooth.");
add_instruction(&mustard_from_seed_mustard, "Add the reserved liquid from before, bit by bit, until you get a nice smooth consistency.");
add_instruction(&mustard_from_seed_mustard, "Store into a glass jar at room temperature, and enjoy!");
add_serving(&mustard_from_seed_mustard, &mustard_seeds, "60 g");
add_serving(&mustard_from_seed_mustard, &apple_cider_vinegar, "45 ml");
add_serving(&mustard_from_seed_mustard, &water, "20 ml");
add_part(&mustard_from_seed, &mustard_from_seed_mustard);

// no knead bread
Recipe no_knead_bread = create_recipe("no knead bread", basic, "1 loaf", 20190112, 60);
set_description(&no_knead_bread, "<b>No knead bread</b> is the easiest kind of artisan-style bread you can make at home - it requires little effort and absolutely no kneading whatsoever.<br /><br />This forgiving recipe allows for substitutions and additions of ingredients without altering the final texture of the bread. If you bake a whole wheat loaf, you might want to add 1-10 ml of <a href='gluten_flour.html'>gluten flour</a> to help it rise.<br /><br />The bread can be left to rise overnight. I usually mix it at around 9pm, and bake it early the next morning at 8am. Unlike a lot of bread recipes, there is no second rise, and you don't need to knead or monitor it at all, because the gluten develops on its own. It's a recipe that is very hard to fail at, and you'll get good bread every time!<br /><br />We sometimes add spices to the dough, like <a href='chili_pepper_flakes.html'>chili</a> and <a href='garlic.html'>garlic</a>, or we make a more hearty bread by adding <a href='pumpkin_seeds.html'>pumpkin seeds</a> or <a href='sunflower_seeds.html'>sunflower seeds</a>. Experiment with it!<br /><br /><b>Baking without a dutch oven</b><br /><br />If you don't have a cast-iron container with a lid, you can use a baking pan with foil. This is the method I use, because I lack a good lid. While the bread doesn't have a hard top crust, the result is still great (the bottom will be tougher than the top). Coat the bread pan with <i>15 ml (1 tbsp)</i> <a href='olive_oil.html'>olive oil</a>, and sprinkle the inside of the pan with some <a href='cornmeal.html'>cornmeal</a> (enough to cover it all in a thin coat). The cornmeal will keep the dough from sticking to the pan. Bread pans don't keep their temperature as well as cast-iron cookware, so the bread will stick due to lack of heat. Oiling the pan is necessary.<br /><br />");
RecipePart no_knead_bread_bread_preparation = create_part("bread preparation");
add_instruction(&no_knead_bread_bread_preparation, "In a large bowl, mix <i>240 g (2 cups)</i> of all purpose flour, <i>120 g (1 cup)</i> of <a href='spelt_flour.html'>spelt flour</a> (whole wheat works too), <i>5 g (1 tsp)</i> of <a href='salt.html'>salt</a>, and <i>2.5 g (1/2 tsp)</i> of <a href='active_dry_yeast.html'>active dry yeast</a>. Mix well.");
add_instruction(&no_knead_bread_bread_preparation, "Add <i>350 ml (1 1/2 cups)</i> of <a href='water.html'>water</a> gradually, stirring it with a spoon into a cohesive dough. The dough will be wet - that's okay, there is no need to add extra flour.");
add_instruction(&no_knead_bread_bread_preparation, "Put a bag or cloth over the bowl, and let the dough rest <u>anywhere from 9 to 24 hours</u>. Ideally, prepare the dough in the evening and let it rest overnight.");
add_instruction(&no_knead_bread_bread_preparation, "<u>Next morning:</u>, turn the dough onto a lightly floured surface, and form it into a round loaf. Let dough rest for 1 h.");
add_instruction(&no_knead_bread_bread_preparation, "Add your dutch oven on the lowest oven rack and then preheat your oven to <u>230 °C (450 °F)</u>. Pre-heat your container for <u>30 minutes/u>. (To bake <b>without a dutch oven</b>, I have a mini-tutorial in the recipe description above.)");
add_instruction(&no_knead_bread_bread_preparation, "Place the wet dough on a lightly floured surface. Shape the dough into a rough ball. If too wet to handle, put some flour on your hands.");
add_instruction(&no_knead_bread_bread_preparation, "After 30 minutes has passed, carefully take out the dutch oven with oven mitts — careful, it's hot! Lay the dutch oven over a neutral surface (wooden board, kitchen towel etc). Place your bread into the dutch oven. At this point, you can add some seeds overtop of the bread. In this recipe, I used <a href='sesame_seeds.html'>sesame seeds</a>, but <a href='poppy_seeds.html'>poppy seeds</a> or quick oats would also be good. Cover with lid and place back inside oven.");
add_instruction(&no_knead_bread_bread_preparation, "Bake for <u>30 minutes</u>.");
add_instruction(&no_knead_bread_bread_preparation, "After <u>30 minutes</u>, take the lid off, and let it cook uncovered for <u>another 7 minutes</u>. Let cool completely before slicing.");
add_serving(&no_knead_bread_bread_preparation, &all_purpose_flour, "240 g");
add_serving(&no_knead_bread_bread_preparation, &spelt_flour, "120 g");
add_serving(&no_knead_bread_bread_preparation, &salt, "2.5 g");
add_serving(&no_knead_bread_bread_preparation, &water, "350 ml");
add_serving(&no_knead_bread_bread_preparation, &active_dry_yeast, "2.5 g");
add_part(&no_knead_bread, &no_knead_bread_bread_preparation);

// okonomiyaki
Recipe okonomiyaki = create_recipe("okonomiyaki", dinner, "4 servings", 20190221, 20);
set_description(&okonomiyaki, "<b>Okonomiyaki</b> (meaning, 'grilled as you like it') is a Japanese dish, similar to the American omelette, but the main difference is the variation of ingredients. Typical okonomiyaki are made with eggs, and often include meat or fish, because of this it's not something we would make at home. Making it without meat is simple enough, but without eggs? Back when we lived in Tokyo, our experience in cooking with plants was limited, but now we've been doing it long enough that we can think of alternatives with ease.<br /><br />The key ingredient? <a href='Chickpea_flour.html'>Chickpea flour</a>, or 'besan flour'. We make chickpea pancakes, and <a href='scrambled_chickpeas.html'>scrambled chickpeas</a> (resembles scrambled eggs) at home all the time. Chickpea flour is a staple on Pino, and works very well for okonomiyaki.<br /><br /><img src='../media/recipes/okonomiyaki_1.jpg'/><br /><br />If you're in a place were nagaimo (or yamaimo) is available, we highly reccommend adding it to the dish. It makes a fluffier pancake. Although we've made okonomiyaki without nagaimo before, so if you can't find it know that it will work and be very delicious anyway. It imparts little flavour, all it does is add nutrition and texture. Nagaimo, unlike most potatoes, can be eaten raw. However, it is best to handle the nagaimo with gloves, or to soak the peeled tuber in a vinegar-water solution to neutralize irritant <b>oxalate crystals</b> found on their skin. Nagaimo are low-calorie, high in protein, and have potassium, zinc, vitamin C and more. The texture of grated nagaimo can be off-putting, it looks like a regular tuber when whole, but when grated it becomes slime, almost liquid. This sort of texture is well-liked in Japan and referred to as being 'neba neba' (slimy). This texture present in many other foods like okra and nattou. This texture makes it an ideal egg alternative, it can be used to make deserts when baking. I'm thinking it too, could make a good faux-cheese pizza topping.<br /><br />Aonori is another obscure ingredient - again, it can be omitted, although it tastes really amazing with it. We made okonomiyaki without it when we were in Majuro, because it simply wasn't available, so we used finely cut nori instead. Obviously, this isn't a perfect substitution, because aonori is very sweet and tastes nothing like nori. However, nori is still very delicious and pairs well enough with the okonomiyaki.<br /><br /><b>How to make true okonomi sauce</b><br /><br />In this recipe, I don't use true 'okonomi sauce'. Why? Because I don't use many pre-made sauces, I prefer to make my own. Okonomi sauce requires many ingredients, and honestly, the sauce I've made works really well in this recipe and makes a good okonomi sauce alternative.<br /><br />If you want to make your own, you can mix 7g (1 1/2 tsp) <a href='#whole_can_sugar.html'>sugar</a>, 45g (3 tbsp) <b>ketchup</b> and 45g (3 tbsp) of vegan <b>worcestershire sauce</b>. If you are like me, and don't care to buy pre-made sauces but want to avoid buying both ketchup and worcestershire sauce, you can make these too:<br /><br /><b>Worcestershire sauce:</b> combine <a href='apple_cider_vinegar.html'>apple cider vinegar</a>, water, <a href='soy_sauce.html'>soy sauce</a>, <a href='#whole_cane_sugar.html'>sugar</a>, <a href='#mustard_seeds.html'>mustard powder</a>, onion powder, garlic powder, <a href='cinnamon.html'>cinnamon</a> and <a href='black_pepper.html'>black pepper</a> in pan, bring to a boil and cook for a minute, then let cool.<br /><br /><b>Ketchup:</b> Using some <b>fresh tomato sauce</b> may be enough, otherwise add a bit of <a href='#whole_cane_sugar.html'>sugar</a> and <a href='apple_cider_vinegar.html'>apple cider vinegar</a> to it.<br /><br />");
RecipePart okonomiyaki_okonomiyaki = create_part("okonomiyaki");
add_instruction(&okonomiyaki_okonomiyaki, "Peel the <a href='nagaimo.html'>nagaimo</a>, then soak it in a water and vinegar solution (doing this helps to remove irritants). Dry the nagaimo, then grate <i>160 g (6-8cm)</i> of <a href='nagaimo.html'>nagaimo</a> with a fine grater. Do this over a bowl, as the grated nagaimo is very slimy. Keep aside.");
add_instruction(&okonomiyaki_okonomiyaki, "Mince <i>500 g (1 small head)</i> <a href='green_cabbage.html'>green cabbage</a>, keep aside.");
add_instruction(&okonomiyaki_okonomiyaki, "In a bowl, mix <i>110 g (1 cup)</i> of <a href='chickpea_flour.html'>chickpea flour</a>, <i>10 g (1/4 cup)</i> <a href='nutritional_yeast.html'>nutritional yeast</a>, the grated <a href='nagaimo.html'>nagaimo</a>, the minced <a href='green_cabbage.html'>green cabbage</a> and <i>1.25g (1/4 tsp)</i> of <a href='salt.html'>salt</a>. Then, add <i>320 ml (1 1/4 cup)</i> of <a href='water.html'>water</a>, or <a href='shiitake.html'>shiitake</a> dashi. (for shiitake dashi, soak 5-6 shiitake in 320 ml of hot water for <u>15 minutes</u>, or <u>overnight</u> in cold water.)");
add_instruction(&okonomiyaki_okonomiyaki, "Heat a non-stick pan at high heat, add <i>5 ml (1 tsp)</i> of <a href='sesame_oil.html'>sesame oil</a>. If you throw some water on and it starts to sizzle, the pan is hot and you can add <i>1/4</i> of the batter. Alternatively, you can add 1/2, although this makes two very large portions.");
add_instruction(&okonomiyaki_okonomiyaki, "Let okonomiyaki cook for <u>5 minutes</u>, shaking the pan every now and then so the batter doesn't stick.");
add_instruction(&okonomiyaki_okonomiyaki, "After <u>5 minutes</u>, it's time to give the other side some grilling time. Put a plate on top of the pancake, keep your hand on the plate and flip the pan so that the pancake ends up cooked side up on the plate. Then, slide the pancake back into the pan (cooked side up). You can also just flip it with a spatula, but we rather like the plate method :).");
add_instruction(&okonomiyaki_okonomiyaki, "Cook for another <u>5 minutes</u>, then slide onto a plate, repeat process for the rest of the batter.");
add_serving(&okonomiyaki_okonomiyaki, &nagaimo, "160 g, grated");
add_serving(&okonomiyaki_okonomiyaki, &green_cabbage, "500 g, minced");
add_serving(&okonomiyaki_okonomiyaki, &chickpea_flour, "110 g");
add_serving(&okonomiyaki_okonomiyaki, &nutritional_yeast, "10 g");
add_serving(&okonomiyaki_okonomiyaki, &salt, "1.25 g");
add_serving(&okonomiyaki_okonomiyaki, &shiitake, "6");
add_serving(&okonomiyaki_okonomiyaki, &water, "320 ml");
add_serving(&okonomiyaki_okonomiyaki, &sesame_oil, "10 ml");
add_part(&okonomiyaki, &okonomiyaki_okonomiyaki);
RecipePart okonomiyaki_sauce = create_part("sauce");
add_instruction(&okonomiyaki_sauce, "In a small bowl, mix <i>60 ml (4 tbsp)</i> of <a href='soy_sauce.html'>soy sauce</a>, <i>60 ml (4 tbsp)</i> of <a href='mirin.html'>mirin</a>, <i>15 g (1 tbsp)</i> of <a href='granulated_sugar.html'>sugar</a> and <i>15 g (1 tbsp)</i> of <a href='arrowroot_starch.html'>arrowroot starch</a>. Stir well.");
add_instruction(&okonomiyaki_sauce, "Heat a pan at high heat, when hot, add sauce and cook for <u>2-3 minutes</u> until it thickens. Then, divide onto your okonomiyaki.");
add_serving(&okonomiyaki_sauce, &soy_sauce, "60 ml");
add_serving(&okonomiyaki_sauce, &mirin, "60 ml");
add_serving(&okonomiyaki_sauce, &granulated_sugar, "15 g");
add_serving(&okonomiyaki_sauce, &arrowroot_starch, "15 g");
add_part(&okonomiyaki, &okonomiyaki_sauce);
RecipePart okonomiyaki_topping = create_part("topping");
add_instruction(&okonomiyaki_topping, "First, add about <i>15 g (1 tbsp)</i> (per okonomiyaki) of <a href='aonori.html'>aonori</a> on top of the sauce.");
add_instruction(&okonomiyaki_topping, "Then, add some <a href='beni_shouga.html'>beni shouga</a> (pickled red ginger).");
add_instruction(&okonomiyaki_topping, "Finally, top off with some finely chopped <a href='scallions.html'>scallions</a>.");
add_serving(&okonomiyaki_topping, &ao_nori, "30 g");
add_serving(&okonomiyaki_topping, &beni_shouga, "to taste");
add_serving(&okonomiyaki_topping, &scallions, "4 stalks");
add_part(&okonomiyaki, &okonomiyaki_topping);

// pandanus fruit bread
Recipe pandanus_fruit_bread = create_recipe("pandanus fruit bread", dessert, "20 pieces", 20181110, 40);
set_description(&pandanus_fruit_bread, "<b>Recipe location:</b> Majuro, Marshall Islands.<br /><br />We arrived in the <a href='https://100r.co/pages/the_promise_of_pancakes.html' target='_blank'>Marshall Islands 3 weeks ago</a>. We are  settled in, but one thing remains difficult for us: finding fresh produce. The vegetables in Majuro are imported from either Guam, or Hawaii by cargo ship, and because they spend much time at sea they are frozen. Freezing fresh whole vegetables is never a good idea, the vegetables rot before they make it onto the shelves. When possible, we opt for local produce. Majuro has few native fresh vegetables, but they do have some fruit and among these is <b>the pandanus fruit</b>.<br /><br /><img src='../media/recipes/pandanus_fruit_bread_2.jpg'/><br /><br />Pandanus, or <a href='https://en.wikipedia.org/wiki/Pandanus_tectorius' target='_blank'>Pandanus tectorius</a>, bears a fruit that look like a giant pinecone, it is made up of little wedge-like phalanges, commonly referred to as 'keys'. Each fruit has about 40-80 keys, the part of the keys that is attached to the core is soft, and a bright orange colour. In the Marshall Islands, people suck on the orange part which is sweet and tastes of cane sugar. The soft part can also be cut, and processed into juice. It is possible to make the juice yourself, this <b><a href='http://www.cuisinivity.com/globalfeast/pacific/Marshall_Islands/2012/Pandanus.php' target='_blank'>blog post</a></b> explains the process at length.<br /><br />I don't have the space, nor the tools to prepare pandanus juice, but a store in Majuro sells frozen purée. This is what I used to prepare this pandanus fruit cake and the topping that goes with it. What does it taste like? It is similar to mango, sweet, but also bitter.<br /><br /><img src='../media/recipes/pandanus_fruit_bread_1.jpg'/><br /><br />I bought a generous portion of frozen pandanus purée, and couldn't use most of it in the bread so I used the rest to make a topping. The chia seeds don't act as a thickener and can be omitted, but they add nutritional value to the meal.<br /><br />If you ever come across some pandanus fruit, please try it! Making pandanus juice from scratch requires some muscle and serious dedication, but in places where it grows, chances are you'll also find a 'processed' version.<br /><br />Pandanus Tectorius photo credit: U. S. Geological Survey, USGS /Forest & Kim Starr.<br /><br />");
RecipePart pandanus_fruit_bread_pandanus_bread = create_part("pandanus bread");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "Preheat oven to <u>180 °C (350 °F)</u>.");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "Mix <i>2.5 ml (1/2 tsp)</i> of <a href='apple_cider_vinegar.html'>apple cider vinegar</a> in <i>60 ml (1/4 cup)</i> of <a href='soy_milk.html'>soy milk</a>, this will make the milk curdle.");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "In a bowl, combine <i>180 g (1 1/2 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>, <i>60 g (1/2 cup)</i> of <a href='spelt_flour.html'>spelt flour</a>, <i>5 ml (1 tsp)</i> of <a href='baking_powder.html'>baking powder</a>, <i>2.5 ml (1/2 tsp)</i> of <a href='baking_soda.html'>baking soda</a> and <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>. Mix well.");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "In yet, another bowl, cream <i>60 ml (1/4 cup)</i> of <a href='canola_oil.html'>canola oil</a> with <i>100 g (1/2 cup)</i> of <a href='whole_cane_sugar.html'>whole cane sugar</a>. Stir in <i>350 ml</i> of <a href='pandanus.html'>pandanus puree</a> and the curdled <a href='soy_milk.html'>soy milk mixture</a>.");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "Add the wet to the dry ingredients and stir until evenly mixed.");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "Pour the batter into an oiled square baking pan.");
add_instruction(&pandanus_fruit_bread_pandanus_bread, "Bake for <u>45 minutes</u>.");
add_serving(&pandanus_fruit_bread_pandanus_bread, &soy_milk, "60 ml");
add_serving(&pandanus_fruit_bread_pandanus_bread, &apple_cider_vinegar, "2.5 ml");
add_serving(&pandanus_fruit_bread_pandanus_bread, &all_purpose_flour, "180 g");
add_serving(&pandanus_fruit_bread_pandanus_bread, &spelt_flour, "60 g");
add_serving(&pandanus_fruit_bread_pandanus_bread, &baking_powder, "5 g");
add_serving(&pandanus_fruit_bread_pandanus_bread, &baking_soda, "1.25 g");
add_serving(&pandanus_fruit_bread_pandanus_bread, &salt, "2.5 g");
add_serving(&pandanus_fruit_bread_pandanus_bread, &canola_oil, "60 ml");
add_serving(&pandanus_fruit_bread_pandanus_bread, &whole_cane_sugar, "100 g");
add_serving(&pandanus_fruit_bread_pandanus_bread, &pandanus_fruit, "350 ml");
add_part(&pandanus_fruit_bread, &pandanus_fruit_bread_pandanus_bread);
RecipePart pandanus_fruit_bread_topping = create_part("topping");
add_instruction(&pandanus_fruit_bread_topping, "In a saucepan, add <i>250 ml (1 cup)</i> of puréed <a href='pandanus.html'>pandanus</a> (add some water if too thick), <i>5 g (1 tsp)</i> of <a href='chili_flakes.html'>chili flakes</a> and <i>30 g (2 tsp)</i> of <a href='whole_cane_sugar.html'>whole cane sugar</a>. Stir well and bring to a boil.");
add_instruction(&pandanus_fruit_bread_topping, "Reduce heat, cook for <u>5 minutes</u>, then remove from heat.");
add_instruction(&pandanus_fruit_bread_topping, "When cool, stir in <i>30 g (2 tbsp)</i> of <a href='chia_seeds.html'>chia seeds</a>.");
add_instruction(&pandanus_fruit_bread_topping, "Serve with the pandanus bread!");
add_serving(&pandanus_fruit_bread_topping, &pandanus_fruit, "250 ml");
add_serving(&pandanus_fruit_bread_topping, &chili_pepper_flakes, "5 g");
add_serving(&pandanus_fruit_bread_topping, &whole_cane_sugar, "30 g");
add_serving(&pandanus_fruit_bread_topping, &chia_seeds, "30 g");
add_part(&pandanus_fruit_bread, &pandanus_fruit_bread_topping);

// pan fried breadfruit
Recipe pan_fried_breadfruit = create_recipe("pan fried breadfruit", dinner, "4 people", 20170703, 90);
set_description(&pan_fried_breadfruit, "<b>Recipe location:</b> Huahine, Society Islands. French Polynesia.<br /><br />Breadfruit trees are everywhere in French Polynesia, but sometimes the fruit aren’t mature enough to pick. To make things more difficult, grocery stores don’t sell them (they don’t sell fruit at all). The Polynesians don't buy fruit, they don't have to! They have plenty growing on their property. Getting our hands on a breadfruit was no simple task, on every island we would ask the locals, but again the fruit weren’t ready to pick off just yet.<br /><br />Devine and I had breadfruit when we first arrived in Nuku Hiva, the owner of Snack Vaeki prepared some for us. The chef cooked it outside, over hot coals. The taste is hard to describe, it's very potato-ey. We had the cooked breadfruit with some fresh coconut milk - extracted from a fresh coconut before our very eyes. Since then, we’ve been looking to cook it ourselves.<br /><br /><img src='../media/recipes/pan_fried_breadfruit_1.jpg'/><br /><br />Then finally we got our chance! We bought a breadfruit at the Fare street market in Huahine. The seller at the fruit stand gave it a few knocks, and told us it was ready to eat! It was delicious with coconut milk, but we wanted to try something different. We cut the breadfruit into wedges and pan-fried it. We then served it with some button mushrooms, coated with sweetened soy sauce.<br /><br />");
RecipePart pan_fried_breadfruit_breadfruit = create_part("breadfruit");
add_instruction(&pan_fried_breadfruit_breadfruit, "Preheat oven to <u>220 °C (425 °F)</u>.");
add_instruction(&pan_fried_breadfruit_breadfruit, "Brush outside of <a href='breadfruit.html'>breadfruit</a> with <a href='olive_oil.html'>olive oil</a>.");
add_instruction(&pan_fried_breadfruit_breadfruit, "Wrap in aluminum foil, and bake for <u>1 hour</u>.");
add_instruction(&pan_fried_breadfruit_breadfruit, "To check if it is ready, poke a knife through. If it is soft, it is ready.");
add_instruction(&pan_fried_breadfruit_breadfruit, "Remove foil, and peel skin away. Then, cut in half and scoop out the seed in the middle.");
add_instruction(&pan_fried_breadfruit_breadfruit, "Cut into wedges, and sautée in a pan with a bit of oil. Cook both sides until crispy and golden. Season with some salt and pepper.");
add_serving(&pan_fried_breadfruit_breadfruit, &breadfruit, "1, medium");
add_serving(&pan_fried_breadfruit_breadfruit, &olive_oil, "30 ml");
add_part(&pan_fried_breadfruit, &pan_fried_breadfruit_breadfruit);
RecipePart pan_fried_breadfruit_toppings = create_part("toppings");
add_instruction(&pan_fried_breadfruit_toppings, "Mix <i>60 ml (4 tbsp)</i> <a href='soy_sauce.html'>soy sauce</a> and <i>10 g (2 tsp)</i> of <a href='granulated_sugar.html'>sugar</a> in a pan, stir, and cook on medium-heat for a few minutes. Add <i>230g</i> of <a href='button_mushrooms.html'>button mushrooms</a>, and stir to coat them with the sauce. Serve over breadfruit wedges.");
add_serving(&pan_fried_breadfruit_toppings, &button_mushrooms, "230g");
add_serving(&pan_fried_breadfruit_toppings, &soy_sauce, "50 ml");
add_serving(&pan_fried_breadfruit_toppings, &granulated_sugar, "10 g");
add_part(&pan_fried_breadfruit, &pan_fried_breadfruit_toppings);

// papaya bruschetta topping
Recipe papaya_bruschetta_topping = create_recipe("papaya bruschetta topping", sidedish, "2-3 people", 20171920, 5);
set_description(&papaya_bruschetta_topping, "<b>Recipe location:</b> Nuku Hiva, Marquesas. French Polynesia.<br /><br />Before we arrived in the Marquesas, people made a point of telling us how difficult it was to find vegetables there. We heard that if we wanted tomatoes, we would need to get up at 4am to get them at the market. Devine & I are early risers, but we lack the will to take the dinghy to shore in the dark. It's just as well, because others we met who had tried, came back empty-handed. In the Marquesas, most vegetables are brought in by supply ships, you can never be certain of what you're going to get. Potatoes, cucumbers, cabbages and eggplants we had plenty of, but vegetables like tomatoes, pumpkins, bok choy and salad greens were like rare pokemon.<br /><br />We'd only ever made bruschetta topping with tomatoes, never thought of changing it for anything else. But then I thought, what looks like tomatoes? Papaya! They have a similar reddish tint, comparable texture too. Papaya is something Nuku Hiva has plenty of, and as it turns out, we prefer it over tomatoes now. Papaya is more firm and sweet, and pairs well with balsamic vinegar. I imagine it would taste great with mangoes too, it's something I may try when I get a craving again.<br /><br />If something isn't available, or is too expensive where you are (hunting for raspberries in Japan comes to mind), swap it out for something else. Not every ingredient will work, but it's fun to try isn't it?<br /><br />");
RecipePart papaya_bruschetta_topping_bruschetta = create_part("bruschetta");
add_instruction(&papaya_bruschetta_topping_bruschetta, "Peep skin from <a href='papaya.html'>papaya</a>, scoop out the seeds insides and cut the flesh into small cubes. NOTE: You can wash and dry the papaya seeds to make pepper! Or process them into juice with other fruits.");
add_instruction(&papaya_bruschetta_topping_bruschetta, "Mix <i>30 ml (2 tbsp)</i> of <a href='olive_oil.html'>olive oil</a>, <i>30 ml (2 tbsp)</i> of <a href='balsamic_vinegar.html'>balsamic vinegar</a>, <i>15 g (1 tbsp)</i> of <a href='dried_basil.html'>dried basil</a> and a pinch of <a href='salt.html'>salt</a> together.");
add_instruction(&papaya_bruschetta_topping_bruschetta, "Pour over cubed papaya and serve with homemade <a href='crackers.html'>crackers</a>.");
add_serving(&papaya_bruschetta_topping_bruschetta, &papaya, "1");
add_serving(&papaya_bruschetta_topping_bruschetta, &olive_oil, "30 ml");
add_serving(&papaya_bruschetta_topping_bruschetta, &balsamic_vinegar, "30 ml");
add_serving(&papaya_bruschetta_topping_bruschetta, &dried_basil, "15 g");
add_part(&papaya_bruschetta_topping, &papaya_bruschetta_topping_bruschetta);

// pate chinois
Recipe pate_chinois = create_recipe("pate chinois", dinner, "4 servings", 20150310, 40);
set_description(&pate_chinois, "Whenever I ask Devine what he wants to eat, he'll always say pasta. I don't always say yes to this, not unless I want to eat pasta everyday. Second, he'll ask for Pâté Chinois. It's hard for me to say no to that, it's one of my favourite childhood meals, so I succumb, peek into the fridge and see what variation of it I can make. It's a super versatile dish, my recipe has layers consisting of tofu and burmese tofu, green peas, sweet potato and cauliflower.<br /><br /><img src='../media/recipes/pate_chinois_1.jpg'/><br /><br />Pâté Chinois is very much like Shepherd's pie, I'm not sure how it got that name, I've researched it but no one knows its true origins. Quotes from Wikipedia: '...one possible explanation for the Chinese reference is that it was introduced to Canadian railway workers by Chinese cooks during the building of the North American railroads in the late 19th century'. I guess we'll never know.<br /><br />My mom made this all the time when I was a kid - she would make the traditional meat version with creamed corn. My meals, as you know, are always meatless, and I now have an aversion to creamed corn, but the spirit of the dish remains. It inspires the same kind of comfort.<br /><br /><b>Substitutions:</b><br /><br />I've made versions of this dish with just cauliflower, or just using tofu, or just potatoes too. I've made it so often, I've gone through every possibility. All in all, it consists of a protein layer (tofu, burmese tofu, lentils etc) veggie layer (corn or peas), and topped with a doughy vegetable (potatoes, pumpkin, parnisps, cauliflower etc).<br /><br />I've also made a version which consists of cubed, cooked potatoes (or pumpkin), with scrambled chickpea flour and corn — as I've said, it's always good and can take on different forms!<br /><br />");
RecipePart pate_chinois_pate_chinois = create_part("pate chinois");
add_instruction(&pate_chinois_pate_chinois, "Preheat oven to <u>190 °C (375 °F)</u>.");
add_instruction(&pate_chinois_pate_chinois, "Cut <i>1 large</i> <a href='sweet_potato.html'>sweet potato</a> into cubes, and chop <i>1 head</i> <a href='white_cauliflower.html'>white cauliflower</a> into bite-sized pieces. Bring a pot of <a href='water.html'>water</a> (or <a href='vegetable_bouillon.html'>vegetable bouillon</a> for added flavour) to a boil, add chopped sweet potato and cauliflower and cook until tender.");
add_instruction(&pate_chinois_pate_chinois, "Drain, mix with <i>5 g (1 tsp)</i> of <a href='smoked_paprika.html'>smoked paprika</a>, and purée with an immersion blender. Season with <i>1.25 g</i> of <a href='salt.html'>salt</a> and <a href='black_pepper.html'>black pepper</a>. Set aside.");
add_instruction(&pate_chinois_pate_chinois, "Take <i>454 g (1 block)</i> of firm <a href='tofu.html'>tofu</a>, cut it into fours, then mash it down using your hands. I like to squish the tofu down with my fingers to get a crumbly texture. You can also grate the tofu with a grater. (For a soy-free Pâté Chinois you can also use <a href='Sweet_Mock_Eel_Nigiri.html'>burmese tofu</a>.)");
add_instruction(&pate_chinois_pate_chinois, "In a large pan, brown <i>1 small</i> <a href='yellow_onion.html'>yellow onion</a> with <i>5 ml (1 tsp)</i> of <a href='olive_oil.html'>olive oil</a>. Add the <a href='tofu.html'>crumbled tofu</a>, <i>30 ml (2 tbsp)</i> of <a href='soy_sauce.html'>soy sauce</a> and <i>1 drop</i> of <a href='liquid_smoke.html'>liquid smoke</a>. Cook for a few minutes. Season with black pepper and sea salt.");
add_instruction(&pate_chinois_pate_chinois, "When cooked, press tofu mix into bottom of an ~8x8 baking dish.");
add_instruction(&pate_chinois_pate_chinois, "Cover tofu mix with the contents of <i>400 g (1 can)</i> of of <a href='green_peas.html'>green peas</a> (reserve a few for the top) and top with the puréed cauliflower/sweet potato mixture.");
add_instruction(&pate_chinois_pate_chinois, "Smooth with a spatula, decorate with some <a href='green_peas.html'>green peas</a> and sprinkle with <a href='smoked_paprika.html'>smoked paprika</a>.");
add_instruction(&pate_chinois_pate_chinois, "Bake for <u>30 minutes</u>, then broil on high for <u>3-5 minutes</u> to brown the top. Let rest for <u>10 minutes</u> before serving.");
add_instruction(&pate_chinois_pate_chinois, "Serve with some <a href='sambal_oelek.html'>sambal oelek</a> or some other spicy condiment! Keeps in the fridge for 5 days or can be frozen for 2-3 months.");
add_serving(&pate_chinois_pate_chinois, &white_cauliflower, "1 head, small");
add_serving(&pate_chinois_pate_chinois, &sweet_potatoes, "1, large");
add_serving(&pate_chinois_pate_chinois, &green_peas, "400 g");
add_serving(&pate_chinois_pate_chinois, &tofu, "454 g, firm");
add_serving(&pate_chinois_pate_chinois, &soy_sauce, "30 ml");
add_serving(&pate_chinois_pate_chinois, &yellow_onion, "1, small");
add_serving(&pate_chinois_pate_chinois, &smoked_paprika, "5 g");
add_part(&pate_chinois, &pate_chinois_pate_chinois);

// persimmon curry
Recipe persimmon_curry = create_recipe("persimmon curry", dinner, "2 servings", 20150103, 30);
set_description(&persimmon_curry, "I've been looking for ways to incorporate persimmon into savoury recipes. I always thought it could make a great sauce for pasta or rice meals. If puréed, it will taste very sweet, but if you mix in curry powder and other spices and ingredients it becomes less of a dessert.<br /><br />Be careful when selecting your persimmon, there are two varieties. Hachiya persimmon are more elongated and you need to wait for it to soften down before attempting to eat it. Fuyu persimmon, have a tomato-like shape and you can eat it like an apple.<br /><br />There are many different varieties of curry powders it's just a pre-mixed combination of different ground spices. If you're in a hurry, buying a mix is best. But if you have a full stocked spice rack, it may be better and more fun to do it yourself. Typically curry mixes have turmeric, coriander, cumin, black and red bell pepper, cinnamon cloves, fennel seeds, cardamom, ginger and fenugreek. There can be as much as 20 different spices, but you can probably omit a few and it'll still taste pretty great.<br /><br />Enjoy over some basmasti rice.<br /><br />");
RecipePart persimmon_curry_rice = create_part("rice");
add_instruction(&persimmon_curry_rice, "Rinse <i>100 g (1/2 cup)</i> of <a href='basmati_rice.html'>basmati rice</a> under cold water, transfer to pot.");
add_instruction(&persimmon_curry_rice, "Boil some water, pour <i>180 ml</i> of it over the rice. Bring pot to a boil. Add <i>1</i> <a href='bay_leaf.html'>bay leaf</a>, lower heat and cover. Simmer for <u>15 minutes</u>, remove from heat and let steam for an additional <u>5 minutes</u> with the lid on.");
add_instruction(&persimmon_curry_rice, "Remove <a href='bay_leaf.html'>bay leaf</a> and serve.");
add_serving(&persimmon_curry_rice, &basmati_rice, "100 g");
add_serving(&persimmon_curry_rice, &water, "180 ml");
add_serving(&persimmon_curry_rice, &bay_leaf, "1");
add_part(&persimmon_curry, &persimmon_curry_rice);
RecipePart persimmon_curry_sauce = create_part("sauce");
add_instruction(&persimmon_curry_sauce, "Scoop the flesh out of <i>2 ripe</i> <a href='hachiya_persimmons.html'>hachiya persimmons</a>, purée with a hand blender.");
add_instruction(&persimmon_curry_sauce, "Sauté <i>1 chopped</i> <a href='onion.html'>yellow onion</a>, <i>2 minced</i> <a href='garlic.html'>garlic cloves</a>, <i>2.5 cm (1 inch)</i> of minced <a href='ginger_root.html'>ginger root</a> with a bit of <a href='olive_oil.html'>olive oil</a> in a pan over medium heat. Cook until onions become translucent.");
add_instruction(&persimmon_curry_sauce, "Add <i>2 diced</i> <a href='carrots.html'>carrots</a>,<i>15 g (handful)</i> of <a href='spinach.html'>spinach</a>. Stir for <u>2 minutes</u>, then add <i>15 g (1 tbsp)</i> of <a href='curry_powder.html'>curry powder</a>. Cook for an additional minute.");
add_instruction(&persimmon_curry_sauce, "Add puréed persimmon, cook for <u>5 minutes</u> and then season with <i>1.25 ml (1/4 tsp) of <a href='salt.html'>salt</a>. Serve over <a href='rice.html'>basmati rice</a>.");
add_serving(&persimmon_curry_sauce, &hachiya_persimmon, "2");
add_serving(&persimmon_curry_sauce, &curry_powder, "15 g");
add_serving(&persimmon_curry_sauce, &ginger_root, "2.5 cm, minced");
add_serving(&persimmon_curry_sauce, &garlic, "2 cloves");
add_serving(&persimmon_curry_sauce, &carrots, "2");
add_serving(&persimmon_curry_sauce, &salt, "1.25 g");
add_serving(&persimmon_curry_sauce, &yellow_onion, "1");
add_serving(&persimmon_curry_sauce, &spinach, "15 g");
add_part(&persimmon_curry, &persimmon_curry_sauce);

// potato gnocchi
Recipe potato_gnocchi = create_recipe("potato gnocchi", pasta, "6 servings", 20150121, 60);
set_description(&potato_gnocchi, "A few weeks ago, I asked people what they wanted to see me cook. Some asked for gnocchi, so here they are! Made from scratch, beautiful and black (just because). Topped with a light and sweet sauce, fresh scallions and daikon!<br /><br />As it turns out, making gnocchi is long. It's well worth the effort, but if you're planning on making some, clear your afternoon!<br /><br />Every step takes time. You have to wait for the potatoes to bake, wait for them to cool, you need to remove the skins etc. Skipping any of those steps will result in a gnocchi disaster.<br /><br />These turned out perfect! This is a large recipe, so if you're only two you'll have plenty left-over that you can let dry, freeze and eat later.<br /><br /><img src='../media/recipes/potato_gnocchi_2.jpg'/><br /><br />Because the sauce and toppings are light and simple, you can focus on the texture of the gnocchi.<br /><br />");
RecipePart potato_gnocchi_gnocchi = create_part("gnocchi");
add_instruction(&potato_gnocchi_gnocchi, "Preheat oven to <u>200 °C (400 °F)</u>.");
add_instruction(&potato_gnocchi_gnocchi, "Bake potatoes until fork tender. Peel skins off and mash them up with a fork or food processor. Let cool completely (you can also boil them until fork tender).");
add_instruction(&potato_gnocchi_gnocchi, "Sift <i>150g (1 1/4 cup)</i> of <a href='whole_wheat_flour.html'>whole wheat flour</a>, <i>15 g (1 tbsp)</i> <a href='bamboo_charcoal_powder.html'>bamboo charcoal powder</a> and <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> together in a bowl.");
add_instruction(&potato_gnocchi_gnocchi, "Mix the potatoes in gradually, and knead until you get a consistent dough.");
add_instruction(&potato_gnocchi_gnocchi, "Sprinkle flour on your working surface, and divide your ball of dough into 4. Roll each section into a long rope with an approximate <i>2 cm diameter</i>.");
add_instruction(&potato_gnocchi_gnocchi, "Proceed to cutting up the ropes into <i>2 cm sections</i>.");
add_instruction(&potato_gnocchi_gnocchi, "To make the little lines over the gnocchis (optional, but fun), take each piece and make a ball with it. Press it up against the backside of a fork and slide it down all the way to the ends with your finger. Your ball will be left with sets of lines on top and will take its signature elongated shape.");
add_instruction(&potato_gnocchi_gnocchi, "Cook gnocchi in a pot of boiling water with salt, the bits that are ready will float back to the surface.");
add_serving(&potato_gnocchi_gnocchi, &russet_potatoes, "3 medium");
add_serving(&potato_gnocchi_gnocchi, &whole_wheat_flour, "150 g");
add_serving(&potato_gnocchi_gnocchi, &bamboo_charcoal_powder, "15 g");
add_serving(&potato_gnocchi_gnocchi, &salt, "1.25 g");
add_part(&potato_gnocchi, &potato_gnocchi_gnocchi);
RecipePart potato_gnocchi_sauce = create_part("sauce");
add_instruction(&potato_gnocchi_sauce, "Mix <i>90 ml (6 tbsp)</i> of <a href='soy_sauce.html'>soy sauce</a>, <i>45 ml (3 tbsp)</i> of <a href='mirin.html'>mirin</a> and <i>20 g (4 tsp)</i> of <a href='granulated_sugar.html'>sugar</a> in a bowl.");
add_serving(&potato_gnocchi_sauce, &soy_sauce, "90 ml");
add_serving(&potato_gnocchi_sauce, &mirin, "45 ml");
add_serving(&potato_gnocchi_sauce, &granulated_sugar, "20 g");
add_part(&potato_gnocchi, &potato_gnocchi_sauce);
RecipePart potato_gnocchi_toppings = create_part("toppings");
add_instruction(&potato_gnocchi_toppings, "Rehydrate <i>45 g (3 tbsp)</i> of <a href='wakame.html'>wakame</a> as well in a separate cup. Let stand for <u>5 minutes</u>, drain, rinse and cut into smaller pieces.");
add_instruction(&potato_gnocchi_toppings, "Chop up <i>5 </i> <a href='scallions.html'>scallion stalks</a> and grate <i>5 cm</i> of <a href='daikon.html'>daikon</a>. Squeeze water out of daikon, and set aside.");
