// anise bread with sweet pear sauce
Recipe anise_bread_with_sweet_pear_sauce = create_recipe("anise bread with sweet pear sauce", sweet, "2 mini loaves", 20150514, 40);
set_description(&anise_bread_with_sweet_pear_sauce, "<p>Since getting those mini silicone pans, we've been making mini everything. This time, we made some anise sweet bread topped with a sauce, and loaded with caramelized pear chunks.</p><p>My bag of anise seeds has been sitting in my pantry, for way too long, begging for purpose. We are happy to report that the taste of anise pairs very well with pears.</p>");
RecipePart anise_bread_with_sweet_pear_sauce_bread = create_part("bread");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Preheat oven to <u>160°C (325°F)</u>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Put <i>7 g (1 tbsp)</i> of <a href='ground_flax_seeds.html'>ground flax seeds</a> with <i>45 ml (3 tbsp)</i> of <a href='water.html'>water</a>, let thicken for <u>5 minutes</u> (this is your flax 'egg'). Mix in <i>50 g (1/4 cup)</i> of <a href='granulated_sugar.html'>sugar</a> and stir until dissolved. Add <i>3 g (1 tsp)</i> of <a href='ground_anise_seeds.html'>ground anise seeds</a>, and mix once more.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Add <i>90 g (3/4 cup)</i> of <a href='all_purpose_flour.html'>all purpose flour</a> to a bowl with <i>3 g (1 1/2 tsp)</i> of <a href='baking_powder.html'>baking powder</a>. Mix.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Pour the wet ingredients into the dry ones, while gradually adding a bit over <i>150 ml</i> of <a href='soy_milk.html'>soy milk</a>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_bread, "Transfer evenly, to 2 mini loaf pans.");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &flax_seeds, "7 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &water, "45 ml");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &granulated_sugar, "50 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &anise_seeds, "3 g, ground");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &all_purpose_flour, "90 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &baking_powder, "3 g");
add_serving(&anise_bread_with_sweet_pear_sauce_bread, &soy_milk, "150 ml");
add_part(&anise_bread_with_sweet_pear_sauce, &anise_bread_with_sweet_pear_sauce_bread);
RecipePart anise_bread_with_sweet_pear_sauce_sauce = create_part("sauce");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Cut your <a href='bosc_pear.html'>bosc pear</a> into cubes.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Heat a pan at medium heat, add <i>15 g (1 tbsp)</i> of <a href='vegan_butter.html'>vegan butter</a>(or some neutral vegetable oil). Once melted(or hot, if using oil), add <i>15 g (1 tbsp)</i> of <a href='granulated_sugar.html'>sugar</a> and <i>15 ml (1 tbsp)</i> of <a href='maple_syrup.html'>maple syrup</a>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Once it starts to boil, add the cubed <a href='bosc_pear.html'>bosc pear</a> and lower the heat. Let it bathe in the sweet mixture for <u>3-5 minutes</u>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Pour the sauce over the 2 mini loaves, bake for <u>30 minutes</u>.");
add_instruction(&anise_bread_with_sweet_pear_sauce_sauce, "Let cool and serve!");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &bosc_pear, "1");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &vegan_butter, "15 g");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &granulated_sugar, "15 g");
add_serving(&anise_bread_with_sweet_pear_sauce_sauce, &maple_syrup, "15 ml");
add_part(&anise_bread_with_sweet_pear_sauce, &anise_bread_with_sweet_pear_sauce_sauce);

