// roasted carrots with beluga lentils
Recipe roasted_carrots_with_beluga_lentils = create_recipe("roasted carrots with beluga lentils", maindish, "2 servings", 20141025, 30);
set_description(&roasted_carrots_with_beluga_lentils, "<p>Our oven has been working overtime these days, we've been baking and roasting food almost everyday. We picked up a pack of heirloom carrots at the market last weekend, threw them in the oven with a bit of oil, and served them over a bed of beluga lentils, topped it with a spicy peanut butter sauce.</p><p>Roasted carrots are sweet, a nice change from eating them raw.</p><p>Beluga lentils aren't the cheapest kind you can get, but we like them because they hold their shape well.</p><p><b>Credits:</b> The idea to make a recipe with beluga lentils was inspired by Meike Peters' <a href='https://eatinmykitchen.meikepeters.com/beluga-lentil-salad-with-pear-blue-cheese-and-thyme/' target='_blank'>beluga lentil salad recipe</a>.</p>");
RecipePart roasted_carrots_with_beluga_lentils_carrots = create_part("carrots");
add_instruction(&roasted_carrots_with_beluga_lentils_carrots, "Preheat oven to <u>200 °C (400 °F)</u>");
add_instruction(&roasted_carrots_with_beluga_lentils_carrots, "Wash <i>8</i> organic <a href='heirloom_carrots.html'>heirloom carrots</a>, but do not peel them. Dry the carrots, and then toss them with <i>15 ml (1 tsp)</i> of <a href='olive_oil.html'>olive oil</a> and <i>5 g (1 tsp)</i> of <a href='thyme.html'>dried thyme</a>.");
add_instruction(&roasted_carrots_with_beluga_lentils_carrots, "Lay on a baking sheet and sprinkle with <i>1.25g (1/4 tsp)</i> of <a href='salt.html'>salt</a> and some <a href='black_pepper.html'>black pepper</a>. Bake for <u>25 minutes</u>, making sure to flip the carrots halfway through.");
add_serving(&roasted_carrots_with_beluga_lentils_carrots, &heirloom_carrots, "8");
add_serving(&roasted_carrots_with_beluga_lentils_carrots, &olive_oil, "15 ml");
add_serving(&roasted_carrots_with_beluga_lentils_carrots, &thyme, "5 g");
add_serving(&roasted_carrots_with_beluga_lentils_carrots, &sea_salt, "5 g");
add_serving(&roasted_carrots_with_beluga_lentils_carrots, &black_pepper, "pinch");
add_part(&roasted_carrots_with_beluga_lentils, &roasted_carrots_with_beluga_lentils_carrots);
RecipePart roasted_carrots_with_beluga_lentils_sauce = create_part("sauce");
add_instruction(&roasted_carrots_with_beluga_lentils_sauce, "Mix <i>30 ml (2 tbsp)</i> of <a href='sriracha.html'>sriracha</a>, <i>33 g (2 tbsp)</i> <a href='peanut_butter.html'>peanut butter</a>, <i>60 ml (1/4 cup)</i> <a href='soy_sauce.html'>soy sauce</a> and <i>30 ml (2 tbsp)</i> <a href='japanese_rice_vinegar.html'>Japanese rice vinegar</a> in a small bowl.");
add_serving(&roasted_carrots_with_beluga_lentils_sauce, &sriracha, "30 ml");
add_serving(&roasted_carrots_with_beluga_lentils_sauce, &peanut_butter, "33 g");
add_serving(&roasted_carrots_with_beluga_lentils_sauce, &soy_sauce, "60 ml");
add_serving(&roasted_carrots_with_beluga_lentils_sauce, &japanese_rice_vinegar, "30 ml");
add_part(&roasted_carrots_with_beluga_lentils, &roasted_carrots_with_beluga_lentils_sauce);
RecipePart roasted_carrots_with_beluga_lentils_lentils = create_part("lentils");
add_instruction(&roasted_carrots_with_beluga_lentils_lentils, "Wash <i>115 g (1/2 cup)</i> of dry <a href='beluga_lentils.html'>beluga lentils</a>. Put <i>375 ml (~1 1/2 cup)</i> of <a href='vegetable_bouillon.html'>vegetable bouillon</a> in a pot and add the <a href='lentils.html'>lentils</a>, bring to a boil.");
add_instruction(&roasted_carrots_with_beluga_lentils_lentils, "Reduce heat to <u>medium</u> and simmer for <u>25 minutes</u> — don't let them cook for too long or they'll get too mushy. If the lentils seem cooked but haven't absorbed all of the water, strain them out.");
add_instruction(&roasted_carrots_with_beluga_lentils_lentils, "Set the beluga lentils on a plate with a bit of sauce and top with 4 roasted carrots!");
add_serving(&roasted_carrots_with_beluga_lentils_lentils, &beluga_lentils, "115 g");
add_serving(&roasted_carrots_with_beluga_lentils_lentils, &vegetable_bouillon, "375 ml");
add_part(&roasted_carrots_with_beluga_lentils, &roasted_carrots_with_beluga_lentils_lentils);

// roasted eggplant dip
Recipe roasted_eggplant_dip = create_recipe("roasted eggplant dip", sidedish, "4 servings", 20090206, 50);
set_description(&roasted_eggplant_dip, "<p>A simple roasted eggplant dip recipe. Roasting the garlic is optional, but it gives the dip a deeper, more complex flavor, while diminishing the pungency of the garlic.</p><p>This recipe is ideal when served with flat bread or raw vegetables, it's also good if served with <a href='crackers.html'>crackers</a>. Optionally, you can add roasted green peppers and other vegetables to further augment the flavor and nutrition of the dip.</p><p><b>Add-ins: </b> Add 20 g (~1/2 cup) of chopped parsley and/or 2 diced tomatoes for extra texture and flavor. For a more complex (and sweet) taste, add 45 ml (~3 tbsp) of pomegranate molasses. You can also use grape molasses.</p><p><b>Garlic: </b> The garlic doesn't need to be roasted, you can use it fresh. If you do this, you will have to use ~4-5 cloves instead of the whole head. Use a mortar and pestle to crush the peeled garlic into a paste before adding it to the rest of the ingredients.</p>");
RecipePart roasted_eggplant_dip_garlic = create_part("roasted garlic");
add_instruction(&roasted_eggplant_dip_garlic, "Preheat oven to <u>240 °C (460 °F)</u>.");
add_instruction(&roasted_eggplant_dip_garlic, "Remove outer leaves of whole garlic head, keep the inner skins on and don't pick the cloves apart. Slice off the tip of each garlic clove, so that some flesh is exposed, and rub the tops and sides with some <a href='olive_oil.html'>vegetable oil</a>.");
add_instruction(&roasted_eggplant_dip_garlic, "Wrap garlic head in tin foil. Keep aside.");
add_serving(&roasted_eggplant_dip_garlic, &garlic, "1, whole head");
add_serving(&roasted_eggplant_dip_garlic, &olive_oil, "5 ml");
add_part(&roasted_eggplant_dip, &roasted_eggplant_dip_garlic);
RecipePart roasted_eggplant_dip_eggplant_dip = create_part("eggplant dip");
add_instruction(&roasted_eggplant_dip_eggplant_dip, "With a fork or knife, poke some holes in <i>2</i> large <a href='eggplant.html'>eggplants</a>, and lay them over a baking tray along with the garlic head in tin foil. Roast garlic head for <u>45 minutes</u>, eggplant may require an additional <u>10 minutes</u> (depending on your oven).");
add_instruction(&roasted_eggplant_dip_eggplant_dip, "Allow the eggplant and the garlic to cool.");
add_instruction(&roasted_eggplant_dip_eggplant_dip, "Remove the flesh from the skins of both the roasted garlic cloves and eggplants. Transfer them to a food processor. Add <i>30 g (2 tbsp)</i> of <a href='tahini.html'>tahini</a>, <i>15 ml (1 tbsp)</i> of <a href='olive_oil.html'>olive oil</a>, <i>45 ml (3 tbsp)</i> of <a href='lemon_juice.html'>lemon juice</a> (or juice of 1 fresh lemon) and <i>2.5 g (1/2 tsp)</i> of <a href='cumin_seeds.html'>ground cumin seeds</a> and process into a smooth puree.");
add_instruction(&roasted_eggplant_dip_eggplant_dip, "Season with <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a>, <a href='black_pepper.html'>black pepper</a> and some <a href='smoked_paprika.html'>smoked paprika</a>.");
add_serving(&roasted_eggplant_dip_eggplant_dip, &eggplant, "2 large");
add_serving(&roasted_eggplant_dip_eggplant_dip, &tahini, "30 g");
add_serving(&roasted_eggplant_dip_eggplant_dip, &olive_oil, "15 ml");
add_serving(&roasted_eggplant_dip_eggplant_dip, &lemon_juice, "45 ml");
add_serving(&roasted_eggplant_dip_eggplant_dip, &cumin_seeds, "2.5 g, ground");
add_serving(&roasted_eggplant_dip_eggplant_dip, &salt, "1.25 g");
add_serving(&roasted_eggplant_dip_eggplant_dip, &black_pepper, "1.25 g");
add_serving(&roasted_eggplant_dip_eggplant_dip, &smoked_paprika, "15 g");
add_part(&roasted_eggplant_dip, &roasted_eggplant_dip_eggplant_dip);

// roasted pumpkin seeds
Recipe roasted_pumpkin_seeds = create_recipe("roasted pumpkin seeds", toppings, "6 servings", 20150118, 30);
set_description(&roasted_pumpkin_seeds, "<p>Our favorite way to eat pumpkin seeds, is to roast them with spices, it makes a nice snack or add-on to soups or other meals.</p><p>Roasted pumpkin seeds are delicious with almost anything, the sweet of the maple syrup with the spicy taste of the paprika is perfect, it is subtle, but very good.</p>");
RecipePart roasted_pumpkin_seeds_pumpkin_seeds = create_part("pumpkin seeds");
add_instruction(&roasted_pumpkin_seeds_pumpkin_seeds, "Preheat oven to <u>150 °C (300 °F)</u>.");
add_instruction(&roasted_pumpkin_seeds_pumpkin_seeds, "Mix <i>10 g (2 tsp)</i> of <a href='smoked_paprika.html'>smoked paprika</a>, <i>10 g (2 tsp)</i> of <a href='ground_cumin.html'>ground cumin</a>, <i>1.25 g</i> of <a href='salt.html'>salt</a> and <i>5 ml (1 tsp)</i> of <a href='maple_syrup.html'>maple syrup</a> together. Add <i>32 g (1/2 cup)</i> of <a href='raw_pumpkin_seeds.html'>raw pumpkin seeds</a>.");
add_instruction(&roasted_pumpkin_seeds_pumpkin_seeds, "Line baking sheet with parchment paper, spread seeds out as flat and as apart from each other as you can on the sheet.");
add_instruction(&roasted_pumpkin_seeds_pumpkin_seeds, "Bake for <u>20 minutes</u>, at the 10 minute mark stir seeds around and then bake for another <u>10 minutes</u>.");
add_instruction(&roasted_pumpkin_seeds_pumpkin_seeds, "Eat as a snack or add as a topping in soups!");
