add_serving(&roasted_pumpkin_seeds_pumpkin_seeds, &smoked_paprika, "10 g");
add_serving(&roasted_pumpkin_seeds_pumpkin_seeds, &cumin_seeds, "10 g");
add_serving(&roasted_pumpkin_seeds_pumpkin_seeds, &maple_syrup, "5 ml");
