// stovetop popcorn
Recipe stovetop_popcorn = create_recipe("stovetop popcorn", basic, "20 cups", 20171108, 5);
set_description(&stovetop_popcorn, "<p>Making popcorn on the stovetop is not a recipe perse, it's a reminder that it's easy to do and that it doesn't require any specialized tools or ingredients. It doesn't require a microwave, just a pot and source of heat (stove).</p><p>When Rek was a kid their family used Jiffy Pop, unpopped kernels, oil, and flavoring agents that come in a heavy-gauge aluminum foil pan. Rek enjoyed seeing the foil rise up into a dome as the kernels started to pop. Then later, their family adopted microwavable bags. These products—while easy and fun—cost more and create unnecessary waste. Because we grew up with packaged popcorn, the idea of trying to pop our own kernels only occurred to us MUCH later in life.</p><p>So, again, this is a reminder that there are conveniences in life that we just don't need.</p>");
RecipePart stovetop_popcorn_popcorn = create_part("popcorn");
add_instruction(&stovetop_popcorn_popcorn, "Put <i>30 ml (2 tbsp)</i> of <a href='olive_oil.html'>olive oil</a> in a deep pot, bring to a high heat.");
add_instruction(&stovetop_popcorn_popcorn, "Add a few corn kernels into the pot. Once they start to pop, add <i>120 g (1/2 cup)</i> of <a href='dry_corn_kernels.html'>dry corn kernels</a> and cover pot with a lid.");
add_instruction(&stovetop_popcorn_popcorn, "Shake pot for 10 seconds, let rest for 10 seconds. Do this until the popping sounds begin to lessen.");
add_serving(&stovetop_popcorn_popcorn, &dry_corn_kernels, "120 g");
add_serving(&stovetop_popcorn_popcorn, &olive_oil, "30 ml");
add_part(&stovetop_popcorn, &stovetop_popcorn_popcorn);
RecipePart stovetop_popcorn_toppings = create_part("toppings");
add_instruction(&stovetop_popcorn_toppings, "Add desired toppings like nutritional yeast, smoked paprika or salt.");
add_serving(&stovetop_popcorn_toppings, &nutritional_yeast, "10 g");
add_serving(&stovetop_popcorn_toppings, &smoked_paprika, "5 g");
add_part(&stovetop_popcorn, &stovetop_popcorn_toppings);

// uzumaki hummus bites
Recipe uzumaki_hummus_bites = create_recipe("uzumaki hummus bites", sidedish, "11 tortillas", 20150429, 50);
set_description(&uzumaki_hummus_bites, "<p><b>Uzumaki</b> means 'spiral' in Japanese, it is also the name of Rek's favorite Junji Ito story. We thought this to be a good name for these savoury beet hummus bites.</p><p>Making tortillas at home is overall easy, but making perfect circles is a challenge and comes with practice. A tortilla press is only ever necessary if you care about the shape. I've gotten good results with a simple rolling pin.</p><p>Making the tortillas black is optional, but it adds a nice contrast to the beet hummus.</p>");
RecipePart uzumaki_hummus_bites_beet_hummus = create_part("beet hummus");
add_instruction(&uzumaki_hummus_bites_beet_hummus, "Preheat oven to <u>190 °C (375 °F)</u>.");
add_instruction(&uzumaki_hummus_bites_beet_hummus, "Wash and cut <i>2 small</i> <a href='red_beets.html'>red beets</a> into 4, rub quarters lightly with <a href='olive_oil.html'>olive oil</a>.");
add_instruction(&uzumaki_hummus_bites_beet_hummus, "Roast for <u>30-40 minutes</u> or until fork tender.");
add_instruction(&uzumaki_hummus_bites_beet_hummus, "Cook <i>125 g (3/4 cup)</i> of dried chickpeas (see <a href='chickpeas.html'>instructions</a>), or use 1 can (15oz)." );
add_instruction(&uzumaki_hummus_bites_beet_hummus, "Purée the roasted beets, with the cooked <i>250 g (1x400g can)</i> <a href='chickpeas.html'>chickpeas</a>, <i>2 minced cloves</i> of <a href='garlic.html'>garlic</a>, a <i>65 g (1/4 cup)</i> of <a href='tahini.html'>tahini</a>, <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> and <i>15 ml (1 tbsp)</i> of <a href='balsamic_vinegar.html'>balsamic vinegar</a>. Mix in <i>30 ml (2 tbsp)</i> of <a href='olive_oil.html'>olive oil</a> at the end. If the hummus feels too thick, add a few tbsp of water until you the desired consistency.");
add_serving(&uzumaki_hummus_bites_beet_hummus, &red_beets, "2");
add_serving(&uzumaki_hummus_bites_beet_hummus, &chickpeas, "250g, cooked");
add_serving(&uzumaki_hummus_bites_beet_hummus, &garlic, "2 cloves, minced");
add_serving(&uzumaki_hummus_bites_beet_hummus, &tahini, "65 g");
add_serving(&uzumaki_hummus_bites_beet_hummus, &sea_salt, "1.25 g");
add_serving(&uzumaki_hummus_bites_beet_hummus, &balsamic_vinegar, "15 ml");
add_serving(&uzumaki_hummus_bites_beet_hummus, &olive_oil, "30 ml");
add_part(&uzumaki_hummus_bites, &uzumaki_hummus_bites_beet_hummus);
RecipePart uzumaki_hummus_bites_tortillas = create_part("tortillas");
add_instruction(&uzumaki_hummus_bites_tortillas, "Put <i>360 g (3 cups)</i> of <a href='all_purpose_flour.html'>all purpose flour</a>, <i>5 g (1 tsp)</i> <a href='bamboo_charcoal_powder.html'>bamboo charcoal powder</a> and <i>1.25 g (1/4 tsp)</i> of <a href='salt.html'>salt</a> in a bowl. Mix well.");
add_instruction(&uzumaki_hummus_bites_tortillas, "Add <i>60 ml (1/4 cup)</i> of <a href='olive_oil.html'>olive oil</a> and <i>180 ml (3/4 cup)</i> of <a href='cold_water.html'>cold water</a>.");
add_instruction(&uzumaki_hummus_bites_tortillas, "Knead into a smooth dough, and separate into <i>11 balls</i>. Dust the balls lightly with flour, and let rest for <u>5 minutes</u>.");
add_instruction(&uzumaki_hummus_bites_tortillas, "Roll the balls out into circles. Put the tortillas under a moist cloth until you're ready to cook them so they don't dry up (I usually spray a bit of water over the cloth).");
add_instruction(&uzumaki_hummus_bites_tortillas, "Heat a non-stick pan at medium heat, cook tortillas for <u>30 seconds</u> on each side. Again, put the warmed tortillas under a cloth so they stay nice and moist.");
add_serving(&uzumaki_hummus_bites_tortillas, &all_purpose_flour, "360 g");
add_serving(&uzumaki_hummus_bites_tortillas, &bamboo_charcoal_powder, "5 g");
add_serving(&uzumaki_hummus_bites_tortillas, &sea_salt, "5 g");
add_serving(&uzumaki_hummus_bites_tortillas, &olive_oil, "60 ml");
add_serving(&uzumaki_hummus_bites_tortillas, &water, "180 ml, cold");
add_part(&uzumaki_hummus_bites, &uzumaki_hummus_bites_tortillas);
RecipePart uzumaki_hummus_bites_filling = create_part("filling");
add_instruction(&uzumaki_hummus_bites_filling, "Brush some beet hummus onto a tortilla, add <i>~150g (1 cup)</i> of chopped <a href='black_olives.html'>black olives</a> and some <a href='arugula.html'>arugula</a> overtop. Be sure to put the fillings near the edge to make it easier to roll.");
add_instruction(&uzumaki_hummus_bites_filling, "Roll the tortillas tightly and then slice into bite-sized bits.");
add_serving(&uzumaki_hummus_bites_filling, &black_olives, "150 g");
add_serving(&uzumaki_hummus_bites_filling, &arugula, "few bunches");
add_part(&uzumaki_hummus_bites, &uzumaki_hummus_bites_filling);

// vegemite caramel
